INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18162, 'Ribociclib', 'Metformin', 'Moderate', 'Coadministration with ribociclib may increase the plasma concentrations and pharmacologic effects of drugs that are substrates of breast cancer resistance protein (BCRP), organic cation transporter-2 (OCT-2), multidrug and toxin extrusion 1 (MATE1), and/or bile salt efflux pump (BSEP) transporters. The proposed mechanism, based on in vitro data, is decreased clearance due to ribociclib-mediated inhibition of these transport proteins at clinically relevant concentrations.', NULL, 'Caution is advised if ribociclib is used concomitantly with drugs that are substrates of BCRP, OCT-2, MATE1, and/or BSEP transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ribociclib is used concomitantly with drugs that are substrates of BCRP, OCT-2, MATE1, and/or BSEP transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever ribociclib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Dulaglutide, Exenatide, Tolbutamide, Miglitol, Glimepiride, More', 'Entrectinib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18177/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Kisqali (ribociclib)." Nov