INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18164, 'Risdiplam', 'Metformin', 'Moderate', 'Coadministration with risdiplam may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of multidrug and toxin extrusion 1 and 2K (MATE1 and MATE2K) proteins. Based on in vitro data, risdiplam and its metabolite are MATE1 and MATE2K transport inhibitors. The proposed mechanism is decreased clearance due to risdiplam-mediated inhibition of MATE1 and/or MATE2K.', NULL, 'Coadministration of risdiplam with drugs that are substrates of MATE1 and/or MATE2K should generally be avoided.', NULL, 'Coadministration of risdiplam with drugs that are substrates of MATE1 and/or MATE2K should generally be avoided. However, if concomitant use is unavoidable, monitor for drug-related toxicities and consider dosage reduction of the MATE1 or MATE2K substrate (based in the labeling of that drug) as needed.', 'Metabolism', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, More', 'Collagenase clostridium histolyticum, Quinine, Hyaluronic acid, Onasemnogene abeparvovec', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18179/', '[1] "Product Information. Evrysdi (risdiplam)." Genentech, South San Francisco, CA.[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767369485);
