INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18179, 'Tafenoquine', 'Metformin', 'Moderate', 'Coadministration with tafenoquine may increase the plasma concentrations of drugs that are substrates of organic cation transporter-2 (OCT2) or multidrug and toxin extrusion (MATE) transporters. Clinical drug interaction studies with tafenoquine and OCT2 and MATE substrates have not been conducted in humans. However, in vitro observations suggest the potential for increased concentrations of these substrates, which may result in increased toxicities. Specifically, tafenoquine inhibited metformin transport via human OCT2, MATE-1, and MATE2-K transporters in vitro.', NULL, 'Concomitant use of tafenoquine with OCT2 and MATE substrates should generally be avoided.', NULL, 'Concomitant use of tafenoquine with OCT2 and MATE substrates should generally be avoided. If coadministration is required, patients should be monitored for drug-related toxicities and dosage reductions considered as needed in accordance with the product labeling of the coadministered drugs.', 'Excretion', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, More', 'Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine, Chloroquine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18194/', '[1] "Product Information. Krintafel (tafenoquine)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.[3] "Product Information. Krintafel (tafenoquine)." GlaxoSmithKline, 