INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18207, 'Warfarin', 'Metformin', 'Moderate', 'Coadministration with metformin may decrease the anticoagulant effects of vitamin K antagonists (VKAs). The mechanism has not been delineated, but it may involve metformin-mediated increase in the elimination rate of VKAs. In addition, concomitant use of VKAs with metformin may increase the risk of severe hypoglycemia. The clinical significance of this interaction is unknown.', NULL, 'Until more information is available, patients receiving VKAs with metformin should be closely monitored during concomitant therapy.', NULL, 'Until more information is available, patients receiving VKAs with metformin should be closely monitored during concomitant therapy. The INR or prothrombin time should be checked frequently and the VKA dosage adjusted accordingly, particularly following initiation, change of dosage, or discontinuation of metformin therapy. In addition, patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations) and to notify their physician if it occurs.', 'Metabolism', 'Acetohexamide, Guar gum, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Ertugliflozin, Semaglutide, Linagliptin, Tolazamide, Empagliflozin, More', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18222/', '[1] Nam YH, Brensinger CM, Bilker WB, Leonard CE, Han X, Hennessy S "Serious hypogl