INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18429, 'Amitriptyline', 'Estradiol', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', NULL, 'The clinical significance of this interaction has not been established.', NULL, 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'Maprotiline, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Vilazodone, Esketamine, Escitalopram', 'Norethisterone, Progesterone, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Norgestrel, Bazedoxifene, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18444/', '[1] Somani SM, Khurana RC "Mechanism of estrogen-imipramine interaction." JAMA 223 (1973): 560[2] Prange AJ, Wilson IC, Alltop LB "Estrogen may well affect response to antidepressant." JAMA 219 (1972): 143-4[3] Edelbroek PM, Zitman FG, Knoppert-van der Klein EA, van Putten PM, de Wolff FA "Therapeutic drug monitoring of amitriptyline: impact of age, smoking and contraceptives on drug and metabolite levels in bulimic women." Clin Chim Acta 165 (1987): 177-87[4] Krishnan KR, France RD, Ellinwood EH, Jr "Tricyclic-in