INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18526, 'Estradiol', 'Elagolix', 'Moderate', 'Since endometriosis is fueled by estrogen, coadministration with estrogen-containing medications including combination oral contraceptive pills is expected to reduce the efficacy of elagolix. The effect of progestin-only contraceptives is unknown. Elagolix is a gonadotropin-releasing hormone (GnRH) receptor antagonist that inhibits endogenous GnRH signaling by binding competitively to GnRH receptors in the pituitary gland. Administration of elagolix results in dose-dependent suppression of luteinizing hormone (LH) and follicle-stimulating hormone (FSH), leading to decreased levels of the ovarian sex hormones, estradiol and progesterone.', NULL, 'Women requiring contraception should be advised to use non-hormonal forms of contraception during treatment with elagolix and for one week after its discontinuation.', NULL, 'Women requiring contraception should be advised to use non-hormonal forms of contraception during treatment with elagolix and for one week after its discontinuation.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18541/', '[1] "Product Information. Orilissa (elagolix)." AbbVie US LLC, North Chicago, IL.[2] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[3] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 1767365203);
