INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18592, 'Estradiol', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Lonafarnib is a potent CYP450 3A time-dependent and mechanism-based inhibitor in vitro.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. The prescribing information recommends avoiding concomitant use of lonafarnib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges when possible. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18607/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18593, 'Estradiol', 'Loracarbef', 'Moderate', 'RECOMMENDED: The effectiveness of estrogen-containing oral contraceptives may be impaired by concomitant treatment with antimicrobial agents. Most antimicrobials, with the exception of the rifamycins and possibly griseofulvin, do not induce hepatic enzymes and have not been shown to significantly increase the clearance of oral contraceptive estrogens. Some investigators believe that antimicrobials interfere with the enterohepatic recirculation of estrogens by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating parent estrogen molecules following first-pass metabolism. It is possible that a small number of women may be more susceptible to contraceptive failure and, consequently, are more sensitive to the effects of antimicrobials on estrogen disposition in vivo, but risk factors or genetic predispositions have yet to be identified.', NULL, 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy.', NULL, 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy. Alternative or additional methods of birth control should be considered during and for at least one week beyond the last dose of short-term antimicrobial therapy, and for at least the initial weeks of long-term antimicrobial therapy when risk may be the greatest.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'ht