INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18706, 'Amprenavir', 'Alosetron', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of alosetron, which has been shown in vitro to be partially metabolized by the isoenzyme.', NULL, 'Because alosetron is associated with potentially serious and life-threatening, dose-related gastrointestinal adverse effects, caution is advised during concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'Because alosetron is associated with potentially serious and life-threatening, dose-related gastrointestinal adverse effects, caution is advised during concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. Patients should be advised to immediately discontinue alosetron and notify their physician if they experience constipation or signs and symptoms of ischemic colitis such as rectal bleeding, bloody diarrhea, and new or worsening abdominal pain. Alosetron should not be resumed if ischemic colitis is diagnosed. Ischemic colitis and other serious complications such as obstruction, perforation, impaction, and toxic megacolon have resulted in hospitalization, blood transfusion, surgery, and death.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18721/', '[1] "Product Information. Lotronex (alosetron)." Glaxo Wellcome, Research Triangle Park, NC.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365203);
