INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18757, 'Alosetron', 'Zileuton', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of alosetron, which has been shown in vivo to be predominantly metabolized by the isoenzyme.', NULL, 'Because alosetron is associated with potentially serious and life-threatening, dose-related gastrointestinal adverse effects, concomitant use with CYP450 1A2 inhibitors should generally be avoided if possible (use with fluvoxamine is specifically contraindicated).', NULL, 'Because alosetron is associated with potentially serious and life-threatening, dose-related gastrointestinal adverse effects, concomitant use with CYP450 1A2 inhibitors should generally be avoided if possible (use with fluvoxamine is specifically contraindicated). If coadministration is required, it may be appropriate to initially prescribe a lower dosage of alosetron (e.g., 1 mg once a day). However, the product labeling does not offer recommendations for a dosage adjustment. Patients should be advised to immediately discontinue alosetron and notify their physician if they experience constipation or signs and symptoms of ischemic colitis such as rectal bleeding, bloody diarrhea, and new or worsening abdominal pain. Alosetron should not be resumed if ischemic colitis is diagnosed. Ischemic colitis and other serious complications such as obstruction, perforation, impaction, and toxic megacolon have resulted in hospitalization, blood transfusion, surgery, and death.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18772/', '[1] "Product Information. Lotronex (alosetron)." Glaxo Wellcome, Research Triangle Park, NC.', 1767365203);
