INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18813, 'Efavirenz', 'Alpelisib', 'Moderate', 'Coadministration with alpelisib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 2B6. The proposed mechanism, based on in vitro data, is increased clearance due to alpelisib-mediated induction of CYP450 2B6.', NULL, 'Caution and monitoring are recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2B6, particularly sensitive CYP450 2B6 substrates (e.', NULL, 'Caution and monitoring are recommended if alpelisib is used concomitantly with drugs that are substrates of CYP450 2B6, particularly sensitive CYP450 2B6 substrates (e.g. bupropion) or those with a narrow therapeutic window. The potential for diminished therapeutic effects should be considered when alpelisib is prescribed in combination with drugs that are CYP450 2B6 substrates.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18828/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Ito D, Amano T, Sato H, Fukuuchi Y "Paroxysmal hypertensive crises induced by selegiline in a patient with Parkinson''s disease." J Neurol 248 (2001): 533-4[4] Pohl R, Balon R, Berchou R "Reaction to chicken nuggets in a patient taking an MAOI." Am J Psychiatry 145 (1988): 651[5] McGrath PJ, Stewart JW, Quitkin FM "A possible L-deprenyl induced hypertensive reaction." J Clin Psychopharmacol 9 (1989): 310-1[6] Tailor SA, Shulman KI, Walker SE, Moss J, Gardner D "Hypertensive episode associated with phenelzine a