INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18814, 'Elagolix', 'Alpelisib', 'Moderate', 'Coadministration of alpelisib with inducers of CYP450 3A4 may reduce the plasma concentrations of alpelisib. The proposed mechanism, based on in vitro data, is increased clearance due to induction of CYP450 3A4, which is one of the routes of metabolism of alpelisib.', NULL, 'Caution is recommended when alpelisib is used with CYP450 3A4 inducers.', NULL, 'Caution is recommended when alpelisib is used with CYP450 3A4 inducers. Pharmacologic effects of alpelisib should be monitored more closely whenever the inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18829/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18815, 'Eltrombopag', 'Alpelisib', 'Moderate', 'Coadministration of inhibitors of the breast cancer resistance protein (BCRP) transporter with alpelisib may increase the plasma concentrations and risk of adverse effects of alpelisib, which is a substrate of this efflux transporter. Alpelisib has been identified as a substrate of BCRP in in vitro studies. However, clinical data are not available.', NULL, 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycaemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia.', NULL, 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycaemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia. Some authorities recommend coadministration of alpelisib with BCRP inhibitors be avoided whenever possible (US).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18830/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
