INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18817, 'Enasidenib', 'Alpelisib', 'Moderate', 'Based on in vitro data, coadministration with enasidenib may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP). The proposed mechanism is decreased clearance due to enasidenib -mediated inhibition of OATP1B1/1B3 and BCRP transporters.', NULL, 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enasidenib is added to or withdrawn from therapy.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18832/', '[1] "Product Information. Idhifa (enasidenib)." Celgene Corporation, Summit, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18818, 'Encorafenib', 'Alpelisib', 'Moderate', 'Coadministration of inhibitors of the breast cancer resistance protein (BCRP) transporter with alpelisib may increase the plasma concentrations and risk of adverse effects of alpelisib, which is a substrate of this efflux transporter. Alpelisib has been identified as a substrate of BCRP in in vitro studies. However, clinical data are not available.', NULL, 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycaemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia.', NULL, 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycaemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia. Some authorities recommend coadministration of alpelisib with BCRP inhibitors be avoided whenever possible (US).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18833/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18819, 'Entrectinib', 'Alpelisib', 'Moderate', 'Coadministration with entrectinib may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter. The proposed mechanisms, based on in vitro data, is decreased clearance due to entrectinib-mediated inhibition of BCRP transport protein.', NULL, 'Caution is advised when entrectinib is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, mitoxantrone, topotecan, and lapatinib.', NULL, 'Caution is advised when entrectinib is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, mitoxantrone, topotecan, and lapatinib. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever entrectinib is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18834/', '[1] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.', 1767365203);
