INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18874, 'Alpelisib', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor. When oral midazolam was administered concomitantly with and at 2 or 4 hours after administration of lefamulin tablets, mean midazolam peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 100% and 200%, respectively.', NULL, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vardenafil, vinca alkaloids). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever oral lefamulin is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18889/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18875, 'Alpelisib', 'Lemborexant', 'Moderate', 'Coadministration of alpelisib with inducers of CYP450 3A4 may reduce the plasma concentrations of alpelisib. The proposed mechanism, based on in vitro data, is increased clearance due to induction of CYP450 3A4, which is one of the routes of metabolism of alpelisib.', NULL, 'Caution is recommended when alpelisib is used with CYP450 3A4 inducers.', NULL, 'Caution is recommended when alpelisib is used with CYP450 3A4 inducers. Pharmacologic effects of alpelisib should be monitored more closely whenever the inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18890/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18876, 'Alpelisib', 'Lenalidomide', 'Moderate', 'Coadministration with alpelisib may theoretically increase the plasma concentrations of drugs that are substrates of organic anion transporter (OAT3), intestinal breast cancer resistance protein (BCRP), and/or P-glycoprotein (P-gp). The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by alpelisib and/or its metabolite (BZG791).', NULL, 'Caution is advised if alpelisib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if alpelisib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever alpelisib is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18891/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[5] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
