INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18887, 'Alpelisib', 'Maraviroc', 'Moderate', 'Coadministration with inhibitors of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may increase the plasma concentrations of maraviroc, which is a substrate of both.', NULL, 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein.', NULL, 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein. Dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18902/', '[1] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365203);
