INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18951, 'Alpelisib', 'Risedronic acid', 'Moderate', 'Coadministration of alpelisib with bisphosphonates or denosumab may increase the risk of developing osteonecrosis of the jaw (ONJ). The condition can occur spontaneously and is generally associated with tooth extraction and/or local infection with delayed healing, particularly in the presence of other risk factors including cancer, chemotherapy, radiotherapy to the head and neck, poor oral hygiene, preexisting dental disease or infection, anemia, or coagulopathy.', NULL, 'Caution is advised when alpelisib is used simultaneously or sequentially with bisphosphonates or denosumab.', NULL, 'Caution is advised when alpelisib is used simultaneously or sequentially with bisphosphonates or denosumab. Treatment should not be initiated in patients with ongoing ONJ from previous or concurrent treatment with bisphosphonates or denosumab. A routine oral examination should be performed by the prescriber prior to initiation of alpelisib treatment. For patients requiring invasive dental procedures, clinical judgment and risk-benefit assessment should guide the management plan of each patient based on their clinical circumstances. Patients should be advised to seek medical attention if they experience signs and symptoms of ONJ, such as: pain in the mouth, teeth, or jaw; swelling or sores inside the mouth; numbness or a feeling of heaviness in the jaw; loosening of a tooth; or exposure of bone in the jaw. Standard medical management should be initiated in patients who develop ONJ.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18966/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18952, 'Alpelisib', 'Ritonavir', 'Moderate', 'Coadministration of inhibitors of the breast cancer resistance protein (BCRP) transporter with alpelisib may increase the plasma concentrations and risk of adverse effects of alpelisib, which is a substrate of this efflux transporter. Alpelisib has been identified as a substrate of BCRP in in vitro studies. However, clinical data are not available.', NULL, 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycaemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia.', NULL, 'If coadministration with BCRP inhibitors is required, patients should be closely monitored for alpelisib-related adverse reactions, including hyperglycaemia, diarrhea, rash, acute kidney injury, abdominal pain, and anemia. Some authorities recommend coadministration of alpelisib with BCRP inhibitors be avoided whenever possible (US).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18967/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (18953, 'Alpelisib', 'Rivaroxaban', 'Moderate', 'Coadministration with alpelisib may theoretically increase the plasma concentrations of drugs that are substrates of organic anion transporter (OAT3), intestinal breast cancer resistance protein (BCRP), and/or P-glycoprotein (P-gp). The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by alpelisib and/or its metabolite (BZG791).', NULL, 'Caution is advised if alpelisib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if alpelisib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever alpelisib is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/18968/', '[1] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[5] "Product Information. Piqray (alpelisib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
