INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38060, 'Amikacin (liposome)', 'Bacitracin', 'Major', 'The combination of a polypeptide antibiotic and an aminoglycoside may increase the risk of neurotoxicity, respiratory paralysis, and renal dysfunction. The mechanism of this interaction is not known, but may involve additive or synergistic effects.', NULL, 'This combination should be avoided concurrently or sequentially.', NULL, 'This combination should be avoided concurrently or sequentially. If coadministration is medically necessary, patients should be closely monitored for development of toxicity, including renal function tests, respiratory function, and vestibular and audiometric tests.', 'Synergism', 'Gatifloxacin, Rifamycin, Lomefloxacin, Ampicillin, Azithromycin, Ofloxacin, Famciclovir, Trifluridine, Moxifloxacin, Benzylpenicillin, Erythromycin, More', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Metronidazole, Methenamine, Spectinomycin, Lefamulin, Nitrofurantoin, Fosfomycin, Tedizolid, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38116/', '[1] "Product Information. Garamycin (gentamicin)." Schering-Plough Corporation, Kenilworth, NJ.[2] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[3] Lindesmith LA, Baines RD Jr, Bigelow DB, Petty TL "Reversible respiratory paralysis associated with polymyxin therapy." Ann Intern Med 68 (1968): 318-27[4] Lee C, de Silva AJ "Interaction of neuromuscular blocking effects of neomycin and polymyxin B." Anesthesiology 50 (1979): 218-20[5] "Product Information. Nebcin (tobramycin)." Lilly, Eli and Company, Indianapolis, IN.[6] "Product Information. Amikin (amikacin)." Bristol-Myers Squibb, Princeton, NJ.[7] Koch-Weser J, Sidel VW, Federman EB, Kanarek P, Finer DC, Eaton AE "Adverse effects of sodium colistimethate: manifestations and specific reaction rates during 317 courses of therapy." Ann Intern Med 72 (1970): 857-68', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38061, 'Bacitracin', 'Atracurium', 'Moderate', 'Like other polypeptide antibiotics, systemically administered bacitracin may prolong apnea and respiratory paralysis associated with the use of nondepolarizing neuromuscular blocking agents. The mechanism may involve decreased intracellular potassium or decreased ionized serum calcium, as intravenous calcium administration has been reported to be helpful in reversing paralysis caused by polymyxin B in some cases.', NULL, 'Patients should be closely monitored for prolonged or excessive neuromuscular blockade.', NULL, 'Patients should be closely monitored for prolonged or excessive neuromuscular blockade.', 'Synergism', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Telavancin, Metronidazole, Methenamine, Lefamulin, Nitrofurantoin, Fosfomycin, Tedizolid, More', 'Botulinum toxin type A, Succinylcholine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38117/', '[1] Kronenfeld MA, Thomas SJ, Turndorf H "Recurrence of neuromuscular blockade after reversal of vecuronium in a patient receiving polymyxin/amikacin sternal irrigation." Anesthesiology 65 (1986): 93-4[2] Huang KC, Heise A, Shrader AK, Tsueda K "Vancomycin enhances the neuromuscular blockade of vecuronium." Anesth Analg 71 (1990): 194-6', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38062, 'Balsalazide', 'Bacitracin', 'Moderate', 'Coadministration of 5-aminosalicylate with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of 5-aminosalicylate or its prodrugs has been associated with rare reports of renal impairment including minimal change nephropathy, acute and chronic interstitial nephritis, and renal failure.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during 5-aminosalicylate therapy.', 'Synergism', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Methenamine, Spectinomycin, Lefamulin, Tedizolid, Dalbavancin, Mupirocin, Rifamycin, More', 'Budesonide, Hydrocortisone, Cromoglicic acid, Prednisone, Prednisolone, Betamethasone, Beclomethasone dipropionate', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38118/', '[1] "Product Information. Canasa (mesalamine (5-aminosalicylic acid))." Axcan Scandipharm Inc, Birmingham, AL.[2] Schreiber S. Hamling J, Zehnter E, et al "Renal tubular dysfunction in patients with inflammatory bowel disease treated with aminosalicylate." Gut 40 (1997): 761-6[3] "Product Information. Lialda (mesalamine (5-aminosalicylic acid))." Shire US Inc, Florence, KY.[4] Novis BH, Korzets Z, Chen P, Bernheim J "Nephrotic syndrome after treatment with 5-aminosalicylic acid." Br Med J (Clin Res Ed) 296 (1988): 1442', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38063, 'Human botulinum neurotoxin A/B immune globulin', 'Bacitracin', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Metronidazole, Methenamine, Spectinomycin, Lefamulin, Nitrofurantoin, Fosfomycin, Tedizolid, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38119/', '[1] MMWR Morb Mortal Wkly Rep "Renal insufficiency and failure associated with immune globulin intravenous therapy -- United States, 1985-1998. Available from: URL: http://www.cdc.gov/mmwr/preview/mmwrhtml/mm4824a3.htm." ([1999 Jun 25]):[2] "Product Information. BabyBIG (botulism immune globulin)." FFF Enterprises, Temecula, CA.[3] Guo X, Nzerue C "How to prevent, recognize, and treat drug-induced nephrotoxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2002 Apr]):[4] Moses S "Nephrotoxic drugs, drug-induced nephrotoxicity. Available from: URL: http://www.fpnotebook.com/Renal/Pharm/NphrtxcDrgs.htm." ([2011 Nov 25]):[5] Perazella MA "Renal vulnerability to drug toxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2009]):[6] Levy JB, Pusey EY "Nephrotoxicity of intravenous immunoglobulin. Available from: URL: http://qjmed.oxfordjournals.org/content/93/11/751.full.pdf+html." ([2000]):[7] "Product Information. Respigam (respiratory syncytial virus immune globulin)." Medimmune Inc, Gaithersburg, MD.[8] "Product Information. Bivigam (immune globulin intravenous)." Biotest Pharmaceuticals Corporation, Boca Raton, FL.[9] "Product Information. Cytogam (cytomegalovirus immune globulin)." CSL Behring, King of Prussia, PA.[10] Naughton CA "Drug-induced nephrotoxicity. Available from: URL: http://www.aafp.org/afp/2008/0915/p743.html." ([2008]):[11] KDIGO. Kidney Disease Improving Global Outcomes "KDIGO clinical practice guideline for acute kidney injury. Available from: URL: http://www.kdigo.org/clinical_practice_guidelines/pdf/KDIGO-AKI-Suppl-Appendices-A-F_March2012.pdf." ([2012 Mar]):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38064, 'Capreomycin', 'Bacitracin', 'Moderate', 'Increased adverse effects are possible when glycopeptide antibiotics are administered concomitantly with other potentially nephrotoxic and neurotoxic drugs.', NULL, 'If these drugs must be used together, renal function, auditory and vestibular function, and serum drug concentrations should be monitored.', NULL, 'If these drugs must be used together, renal function, auditory and vestibular function, and serum drug concentrations should be monitored.', 'Synergism', 'Pyrazinamide, Rifamycin, Bedaquiline, Rifapentine, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, Pyridoxine, More', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Metronidazole, Methenamine, Spectinomycin, Lefamulin, Nitrofurantoin, Fosfomycin, Tedizolid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38120/', '[1] Dubrovskaya Y, Prasad N, Lee Y, Esaian D, Figueroa DA, Tam VH "Risk factors for nephrotoxicity onset associated with polymyxin B therapy." J Antimicrob Chemother 70 (2015): 1903-7[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Vancocin (vancomycin)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38065, 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'Bacitracin', 'Major', 'INTERVAL: The use of live, attenuated cholera vaccine with systemic antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Vibrio cholerae, thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', 'Antagonism', 'Lefamulin, Methenamine, Mupirocin, Lidocaine, Flurbiprofen, Ibuprofen, Chlorhexidine, Povidone-iodine', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38121/', '[1] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.[2] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38066, 'Cidofovir', 'Bacitracin', 'Major', 'Coadministration of cidofovir with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney. Dose-related nephrotoxicity is the major toxicity of cidofovir. Cases of acute renal failure resulting in dialysis and/or contributing to death have occurred with as few as one or two doses of cidofovir.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, cisplatin, clofarabine, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravascular contrast media; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) is considered contraindicated.', 'Synergism', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Metronidazole, Methenamine, Spectinomycin, Lefamulin, Nitrofurantoin, Fosfomycin, Tedizolid, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38122/', '[1] "Product Information. Vistide (cidofovir)." Gilead Sciences, Foster City, CA.', 1767369485);
