INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38068, 'Clofarabine', 'Bacitracin', 'Moderate', 'Coadministration of clofarabine with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney. Moreover, renal impairment secondary to the use of these agents may reduce the clearance of clofarabine, which is primarily eliminated by renal excretion. This may increase the risk of other adverse effects such as nausea, vomiting, stomatitis, hypertension, hypotension, myelosuppression, hemorrhage, and hepatotoxicity.', NULL, 'Drugs that are potentially nephrotoxic (e.', NULL, 'Drugs that are potentially nephrotoxic (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, cidofovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, carboplatin, cisplatin, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) should be avoided during the 5 days of clofarabine administration if possible. Renal function should be evaluated prior to and during therapy, and administration discontinued immediately if substantial increases (e.g., grade 3 or higher) in creatinine are noted.', 'Excretion', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Methenamine, Spectinomycin, Lefamulin, Fosfomycin, Tedizolid, Dalbavancin, Tetracycline, More', 'Fluorouracil, Mercaptopurine, Cladribine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Pralatrexate, Fludarabine, Decitabine, Azacitidine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38124/', '[1] "Product Information. Clolar (clofarabine)." sanofi-aventis, Bridgewater, NJ.', 1767369485);
