INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38104, 'Bacitracin', 'Plazomicin', 'Major', 'The combination of a polypeptide antibiotic and an aminoglycoside may increase the risk of neurotoxicity, respiratory paralysis, and renal dysfunction. The mechanism of this interaction is not known, but may involve additive or synergistic effects.', NULL, 'This combination should be avoided concurrently or sequentially.', NULL, 'This combination should be avoided concurrently or sequentially. If coadministration is medically necessary, patients should be closely monitored for development of toxicity, including renal function tests, respiratory function, and vestibular and audiometric tests.', 'Synergism', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Metronidazole, Methenamine, Spectinomycin, Lefamulin, Nitrofurantoin, Fosfomycin, Tedizolid, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38160/', '[1] "Product Information. Garamycin (gentamicin)." Schering-Plough Corporation, Kenilworth, NJ.[2] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[3] Lindesmith LA, Baines RD Jr, Bigelow DB, Petty TL "Reversible respiratory paralysis associated with polymyxin therapy." Ann Intern Med 68 (1968): 318-27[4] Lee C, de Silva AJ "Interaction of neuromuscular blocking effects of neomycin and polymyxin B." Anesthesiology 50 (1979): 218-20[5] "Product Information. Nebcin (tobramycin)." Lilly, Eli and Company, Indianapolis, IN.[6] "Product Information. Amikin (amikacin)." Bristol-Myers Squibb, Princeton, NJ.[7] Koch-Weser J, Sidel VW, Federman EB, Kanarek P, Finer DC, Eaton AE "Adverse effects of sodium colistimethate: manifes