INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38120, 'Zoledronic acid', 'Bacitracin', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents). Renal function and serum electrolytes should be monitored. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.', 'Synergism', 'Tiludronic acid, Vosoritide, Denosumab, Cholecalciferol, Burosumab, Risedronic acid, Romosozumab', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Methenamine, Spectinomycin, Lefamulin, Fosfomycin, Tedizolid, Dalbavancin, Mupirocin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/