INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38509, 'Diclofenac', 'Balsalazide', 'Moderate', 'Coadministration of 5-aminosalicylate with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of 5-aminosalicylate or its prodrugs has been associated with rare reports of renal impairment including minimal change nephropathy, acute and chronic interstitial nephritis, and renal failure.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during 5-aminosalicylate therapy.', 'Synergism', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, Fluocinolone acetonide, Cortisone, Hydrocortisone, More', 'Beclomethasone dipropionate', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38565/', '[1] "Product Information. Canasa (mesalamine (5-aminosalicylic acid))." Axcan Scandipharm Inc, Birmingham, AL.[2] Schreiber S. Hamling J, Zehnter E, et al "Renal tubular dysfunction in patients with inflammatory bowel disease treated with aminosalicylate." Gut 40 (1997): 761-6[3] "Product Information. Lialda (mesalamine (5-aminosalicylic acid))." Shire US Inc, Florence, KY.[4] Novis BH, Korzets Z, Chen P, Bernheim J "Nephrotic syndrome after treatment with 5-aminosalicylic acid." Br Med J (Clin Res Ed) 296 (1988): 1442', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38510, 'Dicloxacillin', 'Balsalazide', 'Moderate', 'Balsalazide may be rendered less effective when administered with certain antibiotics. Balsalazide is acted upon in the lower gut by bacterial azoreduction to produce mesalamine, the therapeutically active ingredient. Although no studies have been done, it is theorized that oral antibiotics can interfere with this reduction to mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', 'Antagonism', 'Sulbactam, Oxacillin, Piperacillin, Meticillin, Ticarcillin, Mezlocillin, Tazobactam, Nafcillin, Benzylpenicillin, Procaine benzylpenicillin', 'Budesonide, Hydrocortisone, Cromoglicic acid, Olsalazine, Sulfasalazine, Prednisolone, Betamethasone, Beclomethasone dipropionate, Mesalazine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38566/', '[1] "Product Information. Colazal (balsalazide)" Salix Pharmaceuticals, Palo Alto, CA.', 1767369485);
