INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38578, 'Balsalazide', 'Remdesivir', 'Moderate', 'Coadministration of remdesivir with nephrotoxic agents may increase the plasma concentrations of its main metabolite, GS-441524, and the excipient, sulfobutylether-beta-cyclodextrin sodium (SBECD). Both GS-441524 and SBECD are primarily eliminated by the kidneys, thus renal impairment secondary to the use of nephrotoxic agents may reduce their clearance and increase the risk of accumulation.', NULL, 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function.', NULL, 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function. Caution is advised if remdesivir is used in patients who have recently received potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function (estimated glomerular filtration rate, or eGFR, for adult and pediatric patients older than 28 days; serum creatinine for full-term neonates at least 7 days up to 28 days old) should be evaluated prior to starting remdesivir and monitored daily during treatment or as clinically appropriate.', 'Excretion', 'Budesonide, Hydrocortisone, Cromoglicic acid, Prednisone, Prednisolone, Betamethasone, Beclomethasone dipropionate', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, 