INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38959, 'Dexamethasone', 'Bedaquiline', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to the major N-monodesmethyl metabolite, M2. Because M2 is 4- to 6-fold less active than the parent drug in terms of antimycobacterial potency, the interaction may diminish the therapeutic effect of bedaquiline.', NULL, 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Flunisolide, Betamethasone, Phenylephrine, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Ipratropium, Cromoglicic acid, Prednisolone, More', 'Rifamycin, Sulfamethoxazole, Streptomycin, Cycloserine, Pyridoxine, Ethionamide, Aminosalicylic acid', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39015/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[4] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38960, 'Dexamethasone', 'Bempedoic acid', 'Major', 'Concomitant administration of bempedoic acid may potentiate the risk of tendon rupture or injury associated with fluoroquinolones and corticosteroids. The mechanism is unknown. Tendon rupture or injury have most frequently involved the rotator cuff (the shoulder), biceps tendon, or Achilles tendon. Tendon rupture may occur within weeks to months of starting treatment with bempedoic acid.', NULL, 'Caution is recommended if bempedoic acid is prescribed in combination with corticosteroids or fluoroquinolones, particularly in patients with other concomitant risk factors (e.', NULL, 'Caution is recommended if bempedoic acid is prescribed in combination with corticosteroids or fluoroquinolones, particularly in patients with other concomitant risk factors (e.g., age over 60 years, renal failure, previous tendon disorders). Treatment with bempedoic acid should be stopped immediately if rupture of a tendon occurs. Discontinuation of treatment with bempedoic acid should be considered if the patient experiences joint pain, swelling, or inflammation. At the first sign of tendinitis or tendon rupture, the patient should be instructed to rest, avoid exercise, avoid using the affected area, and to contact their healthcare provider. An alternative therapy should be considered in patients with a history of tendon disorders or tendon rupture.', 'Synergism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Cerivastatin, Lomitapide, Alirocumab', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39016/', '[1] "Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics, Ann Arbor, MI.[2] "Product Information. Nexlizet (bempedoic acid-ezetimibe)." Esperion Therapeutics, Ann Arbor, MI.', 1767369485);
