INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38973, 'Dexamethasone', 'Bosentan', 'Moderate', 'Coadministration with bosentan may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C9 and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of those isoenzymes by bosentan.', NULL, 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever bosentan is added to or withdrawn from therapy.', 'Metabolism', 'Tinidazole, Ascorbic acid, Miconazole, Sulfamethoxazole, Sulfamethizole, Povidone-iodine, Sulfisoxazole, Mafenide, Tioconazole, Furazolidone, Lactic acid, More', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Vitamin A, Ipratropium, Cromoglicic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39029/', '[1] "Product Information. Tracleer (bosentan)." Acetelion Pharmaceuticals US, Inc, South San Francisco, CA.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier