INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38975, 'Dexamethasone', 'Brigatinib', 'Major', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', NULL, 'Concomitant use of brigatinib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of brigatinib with moderate CYP450 3A4 inducers should generally be avoided. If coadministration cannot be avoided, the dose of brigatinib should be increased in 30 mg increments as tolerated every 7 days up to a maximum of twice the brigatinib dose that was tolerated prior to initiating the moderate CYP450 3A4 inducer. After discontinuation of the moderate CYP450 3A4 inducer, the brigatinib dose tolerated prior to initiating the moderate CYP450 3A4 inducer should be resumed. The potential for diminished pharmacologic effects of brigatinib should be considered during coadministration with CYP450 3A4 inducers.', 'Metabolism', 'Flunisolide, Betamethasone, Phenylephrine, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Ipratropium, Cromoglicic acid, Prednisolone, More', 'Lenvatinib, Trametinib, Dacomitinib, Tivozanib, Alectinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39031/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[4] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[5] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceutica