INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (19646, 'Hemin', 'Alteplase', 'Moderate', 'Theoretically, hemin may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Hemin has exhibited mild, transient anticoagulant effects during clinical studies.', NULL, 'The use of hemin with concurrent anticoagulant therapy should be avoided.', NULL, 'The use of hemin with concurrent anticoagulant therapy should be avoided. Caution is advised if hemin is used in combination with other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Protein C, Acenocoumarol, Selexipag, Desirudin, Ascorbic acid, Cyclosporine, Sirolimus, Potassium Iodide, Vitamin A, Acetylcysteine, Nandrolone', 'Voxelotor, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19661/', '[1] "Product Information. Panhematin (hemin)." Recordati Rare Diseases Inc, Lebanon, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (19647, 'Heparin', 'Alteplase', 'Major', 'Although this combination is used therapeutically and recommended for certain indications (e.g., acute myocardial infarction, pulmonary embolism), fibrinolytic agents may enhance the anticoagulant effect of heparin. Data have shown that the partial thromboplastin time (PTT) may be significantly increased when fibrinogen concentrations fall in response to thrombolytic therapy. Large studies have reported that the risk of major bleeding is significantly higher when these drugs are given together. Hemorrhages have occurred during heparin infusion, one or more days after thrombolytic administration.', NULL, 'Alteplase is contraindicated in patients with acute ischemic stroke who have received heparin within the last 48 hours and have an elevated activated partial thromboplastin time (aPTT).', NULL, 'Alteplase is contraindicated in patients with acute ischemic stroke who have received heparin within the last 48 hours and have an elevated activated partial thromboplastin time (aPTT). Patients who are to receive a thrombolytic plus heparin for other indications should be carefully selected according to recommended eligibility criteria. The heparin dosage should be adjusted as appropriate to maintain therapeutic aPTT values. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Thrombolytic and/or anticoagulation therapy should be discontinued immediately if serious or uncontrollable bleeding occurs.', 'Synergism', 'Selexipag, Heparin, Nandrolone, Ascorbic acid, Potassium Iodide, Heparin', 'Alteplase, Streptokinase, Antithrombin III human, Tenecteplase, Acenocoumarol, Selexipag, Urokinase, Anistreplase, Reteplase, Alteplase, Ascorbic acid, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19662/', '[1] Sheiman RG, Phillips DA "Combined effects of urokinase and heparin on PTT values during thrombolytic therapy." Angiology 44 (1993): 114-22[2] "Product Information. Retavase (reteplase)." Boehringer Mannheim, Gaithersburg, MD.[3] Gore JM, Sloan M, Price TR, Randall AM, Bovill E, Collen D, Forman S, Knatterud GL, Sopko G, Terrin ML "Intracerebral hemorrhage, cerebral infarction, and subdural hematoma after acute myocardial infarction and thrombolytic therapy in the Thrombolysis in Myocardial Infarction Study. Thrombolysis inMyocardial Infarction, Phase II, pilot and clinical trial." Circulation 83 (1991): 448-59[4] Zahger D, Maaravi Y, Matzner Y, Gilon D, Gotsman MS, Weiss AT "Partial resistance to anticoagulation after streptokinase treatment for acute myocardial infarction." Am J Cardiol 66 (1990): 28-30[5] "Product Information. Activase (alteplase)." Genentech, South San Francisco, CA.[6] Deboer A, Vangriensven JM "Drug interactions with thrombolytic agents: current perspectives." Clin Pharmacokinet 28 (1995): 315-26[7] "Product Information. TNKase (tenecteplase)" Genentech, South San Francisco, CA.[8] Lam XM, Ward CA, Dumee CPRD "Stability and activity of alteplase with injectable drugs commonly used in cardiac therapy." Am J Health Syst Pharm 52 (1995): 1904-9[9] Hirsch J, Dalen J, Guyatt G, American College of Chest Physicians "The sixth (2000) ACCP guidelines for antithrombotic therapy for prevention and treatment of thrombosis. American College of Physicians." Chest 119(1 Suppl) (2001): 1S-2S[10] Harder S, Klinkhardt U "Thrombolytics: drug interactions of clinical significance." Drug Saf 23 (2000): 391-9[11] "Product Information. Abbokinase (urokinase)." Abbott Pharmaceutical, Abbott Park, IL.[12] "Product Information. Streptase (streptokinase)." Astra USA, Westborough, MA.', 1767369485);
