INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (19746, 'Altretamine', 'Cladribine', 'Major', 'The use of cladribine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Cladribine alone may cause severe and prolonged myelosuppression, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'Concomitant use of oral cladribine with immunosuppressive or myelosuppressive agents should be avoided if possible.', NULL, 'Concomitant use of oral cladribine with immunosuppressive or myelosuppressive agents should be avoided if possible. Acute short-term therapy with corticosteroids can be administered. Caution is advised if IV cladribine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. In patients who have previously been treated with immunomodulatory or immunosuppressive drugs, consider potential additive effect, mode of action, and duration of effect of the other drugs prior to initiation of cladribine. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, More', 'Pertuzumab, Vismodegib, Ivosidenib, Belzutifan, Sonidegib, Mitotane, Porfimer sodium, Estramustine, Celecoxib, Aminolevulinic acid, Tazemetostat, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19761/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Leustatin (cladribine)." Ortho Biotech Inc, Raritan, NJ.[3] "Product Information. Leustatin (cladribine)." Ortho Biotech Inc, Raritan, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (19747, 'Altretamine', 'Clomipramine', 'Moderate', 'The coadministration of altretamine with a monoamine oxidase inhibitor (MAOI) or tricyclic antidepressant may result in severe orthostatic hypotension.', NULL, 'Caution is advised if altretamine must be used with a MAOI or tricyclic antidepressant, particularly in elderly patients.', NULL, 'Caution is advised if altretamine must be used with a MAOI or tricyclic antidepressant, particularly in elderly patients. All patients receiving the combination should be advised to avoid rising abruptly from a sitting or lying position and to contact their physician if they experience symptoms of hypotension such as dizziness, lightheadedness, or fainting.', 'Synergism', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, Enasidenib, More', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Viloxazine, Esketamine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19762/', '[1] "Product Information. Hexalen Capsules (altretamine)." US Bioscience, West Conshohoken, PA.[2] Bruckner HW, Schleifer SJ "Orthostatic hypotension as a complication of hexamethylmelamine antidepressant interaction." Cancer Treat Rep 67 (1983): 516', 1767369485);
