INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (19858, 'Alvimopan', 'Venetoclax', 'Moderate', 'Coadministration with venetoclax may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp). The proposed mechanism is decreased clearance due to inhibition of P-glycoprotein-mediated drug efflux in the intestine, liver, and/or kidney by venetoclax.', NULL, 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.', NULL, 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate. Alternatives should be considered whenever possible. The manufacturer recommends that drugs sensitive to P-gp inhibition in the gastrointestinal tract be administered at least 6 hours before venetoclax. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate following the initiation or discontinuation of venetoclax.', 'Others', 'Sorbitol, Tegaserod, Bisacodyl, Lactulose, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Magnesium oxide, Sodium sulfate, Prucalopride, Linaclotide, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19873/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[4] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.', 1767369485);
