INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (19969, 'Ulipristal', 'Norethisterone', 'Moderate', 'RECOMMENDED: Coadministration of ulipristal with combination hormonal contraceptives may reduce the efficacy of both treatments for the prevention of pregnancy. Ulipristal and the progestin component of hormonal contraceptives both bind to the progesterone receptor. Theoretically, competitive inhibition of the receptor may occur, which may diminish the effects of each on ovulation.', NULL, 'Concomitant use of ulipristal with combination hormonal contraceptives should be avoided.', NULL, 'Concomitant use of ulipristal with combination hormonal contraceptives should be avoided. After emergency contraception with ulipristal, routine contraception should be continued or initiated as soon as possible to ensure ongoing prevention of pregnancy. Patients who wish to use progestogen-containing contraceptives should do so no sooner than 5 days after taking ulipristal, and they should use a reliable barrier method of contraception until their next menstrual period.', 'Antagonism', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Estradiol, Ganirelix, Lanreotide, Gonadorelin, Ulipristal, Megestrol acetate, Estradiol, More', 'Norelgestromin, Norethisterone, Etonogestrel, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Medroxyprogesterone acetate, Desogestrel, Dienogest, Estetrol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/19984/', '[1] "Product Information. ella (ulipristal)." Afaxys Inc., Charleston, SC.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefrui