INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20043, 'Amantadine', 'Metoclopramide', 'Moderate', 'Metoclopramide may antagonize the pharmacologic effects of amantadine in the treatment of Parkinson''s disease and drug-induced extrapyramidal reactions. Animal data suggest that amantadine may possess direct and indirect effects on dopamine neurons. In contrast, metoclopramide exhibits central antidopaminergic effects and augments NMDA activity. Adverse effects of metoclopramide include pseudoparkinsonism and extrapyramidal reactions, which are consistent with its pharmacologic effects. Conversely, amantadine may antagonize the actions of metoclopramide on the gastrointestinal tract due to its anticholinergic effects.', NULL, 'Patients receiving amantadine for Parkinson''s disease or drug-induced extrapyramidal reactions should generally avoid antidopaminergic agents including metoclopramide.', NULL, 'Patients receiving amantadine for Parkinson''s disease or drug-induced extrapyramidal reactions should generally avoid antidopaminergic agents including metoclopramide. If these agents have been administered together chronically, amantadine dosing should not be abruptly reduced or discontinued due to the potential to precipitate a parkinsonian crisis, delirium, agitation, delusions, hallucinations, paranoid reaction, stupor, anxiety, depression, slurred speech, and rarely, neuroleptic malignant syndrome. In patients receiving amantadine for influenza A prophylaxis or treatment, the possibility of diminished efficacy of metoclopramide should be considered during coadministration.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20058/', '[1] "Product Information. Reglan (metoclopramide)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] "Product Information. Symmetrel (amantadine)." DuPont Pharmaceuticals, Wilmington, DE.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20044, 'Amantadine', 'Metolazone', 'Minor', 'Limited data indicate that thiazide diuretics and/or triamterene may increase the probability of adverse reactions to amantadine. The mechanism is unknown but may be related to reduced clearance of amantadine, resulting in increased plasma levels. Possible reactions include ataxia, confusion, and agitation. If an interaction is suspected, the dosage of amantadine may be reduced or discontinued, or a different diuretic may be substituted.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20059/', '[1] Wilson TW, Rajput AH "Amantadine-dyazide interaction." Can Med Assoc J 129 (1983): 974-5', 1767365203);
