INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20154, 'Diclofenac (topical)', 'Panobinostat', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20169/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20155, 'Diclofenac (topical)', 'Pegaspargase', 'Moderate', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury. Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults. Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins). Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs. Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20170/', '[1] "Product Information. Oncaspar (pegaspargase)." Rhone-Poulenc Rorer, Collegeville, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Bodmer M, Sulz M, Stadlmann S, Droll A, Terracciano L, Krahenbuhl S "Fatal liver failure in an adult patient with acute lymphoblastic leukemia following treatment with L-asparaginase." Digestion 74 (2006): epub[4] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[5] "Product Information. Elspar (asparaginase)." Merck & Co, Inc, West Point, PA.[6] Christ TN, Stock W, Knoebel RW "Incidence of asparaginase-related hepatotoxicity, pancreatitis, and thrombotic events in adults with acute lymphoblastic leukemia treated with a pediatric-inspired regimen." J Oncol Pharm Pract 24 (2018): 299-308[7] Lu G, Karur V, Herrington JD, Walker MG "Successful treatment of pegaspargase-induced acute hepatotoxicity with vitamin B complex and L-carnitine" Proc (Bayl Univ Med Cent) 29 (2016): 46-7[8] Jenkins R, Perlin E "Severe hepatotoxicity from Escherichia coli L-asparaginase." J Natl Med Assoc 79 (1987): 775-9[9] "Product Information. Asparlas (calaspargase pegol)." Servier, Boston, MA.[10] Burke PW, Aldoss I, Lunning MA, et.al "High-grade PEGylated asparaginase-related hepatotoxicity occurrence in a pediatric-inspired adult acute lymphoblastic leukemia regimen does not necessarily predict recurrent hepatotoxicity in subsequent cycles." Blood 122 (2013): 2671[11] "Product Information. Erwinaze (asparaginase Erwinia chrysanthemi)." EUSA Pharma, Newtown, PA.[12] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[13] Al-Nawakil C, Willems L, Mauprivez C, et.al "Successful treatment of l-asparaginase-induced severe acute hepatotoxicity using mitochondrial cofactors." Leuk Lymphoma 55 (2014): 1670-4', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20156, 'Diclofenac (topical)', 'Peginterferon beta-1a', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20171/', '[1] "Product Information. Plegridy (peginterferon beta-1a)." Biogen Idec Inc, Cambridge, MA.[2] "Product Information. Betaseron (interferon beta-1b)." Berlex, Richmond, CA.[3] "Product Information. Rebif (interferon beta-1a)." Serono Laboratories Inc, Norwell, MA.[4] "Product Information. Avonex (interferon beta-1a)." Biogen, Cambridge, MA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20157, 'Diclofenac (topical)', 'Pentoxifylline', 'Moderate', 'The use of pentoxifylline has been associated with bleeding and/or prolongation in prothrombin time. Theoretically, the risk of bleeding may be increased by concomitant treatment with anticoagulants or other agents that affect hemostasis such as nonsteroidal anti-inflammatory drugs (NSAIDs), platelet inhibitors, thrombin inhibitors, thrombolytic agents, and dextran. There have been postmarketing reports of increased anticoagulant activity in patients receiving vitamin K antagonists in combination with pentoxifylline.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis. Patients on vitamin K antagonists such as warfarin should have more frequent monitoring of INR following initiation or dosage change of pentoxifylline, while patients receiving other agents that affect hemostasis should have periodic examinations for bleeding including hematocrit and/or hemoglobin.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20172/', '[1] "Product Information. Trental (pentoxifylline)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20158, 'Diclofenac (topical)', 'Pexidartinib', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20173/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767365203);
