INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20246, 'Amphotericin B', 'Cisatracurium', 'Moderate', 'Amphotericin commonly causes renal potassium and magnesium wasting. As a result, serum potassium is decreased, and the pharmacologic effects of some neuromuscular blocking agents are enhanced.', NULL, 'To decrease or prevent this interaction, magnesium and potassium levels should be monitored, and the patient''s body stores of potassium and magnesium should be replenished even before the expected fall in serum potassium occurs.', NULL, 'To decrease or prevent this interaction, magnesium and potassium levels should be monitored, and the patient''s body stores of potassium and magnesium should be replenished even before the expected fall in serum potassium occurs.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20261/', '[1] Cushard WG, Kohanim M, Lantis LR "Blastomycosis of bone." J Bone Joint Surg Am 51A (1969): 704-12[2] "Product Information. Amphotec (amphotericin B cholesteryl sulfate)." Sequus Pharmaceuticals Inc, Menlo Park, CA.[3] Miller R, Bates J "Amphotericin B toxicity." Ann Intern Med 71 (1969): 1089-95[4] "Product Information. Abelcet (amphotericin B)." Liposome Company Inc, The, Princeton, NJ.', 1767365203);
