INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20430, 'Ambrisentan', 'Midostaurin', 'Moderate', 'In vitro data suggest that coadministration with potent inhibitors of P-glycoprotein (e.g., cyclosporine) and/or organic anion transporting polypeptide (e.g., cyclosporine, ritonavir) may increase the plasma concentrations of ambrisentan, which is a substrate of these transporters. The interaction has not been studied with other P-gp or OATP inhibitors.', NULL, 'Because ambrisentan is associated with dose-related hepatotoxicity, concomitant use with potent inhibitors of P-gp and/or OATP should be approached with caution.', NULL, 'Because ambrisentan is associated with dose-related hepatotoxicity, concomitant use with potent inhibitors of P-gp and/or OATP should be approached with caution. Patients should be advised to notify their physician if they experience signs and symptoms of liver injury such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20445/', '[1] "Product Information. Letairis (ambrisentan)." Gilead Sciences, Foster City, CA.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365203);
