INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20441, 'Ambrisentan', 'Ospemifene', 'Moderate', 'Ospemifene and its major metabolite, 4-hydroxyospemifene, may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of organic cation transporter (OCT)1, uridine diphosphate glucuronosyltransferase (UGT) 1A3, and/or UGT1A9. The proposed mechanism, based on in vitro data, is decreased clearance due to ospemifene-mediated inhibition of OCT1, UGT1A3, and/or UGT1A9.', NULL, 'Until more information is available, caution is advised when ospemifene is used concomitantly with drugs that are substrates of OCT1, UGT1A3, and/or UGT1A9, particularly those with a narrow therapeutic range.', NULL, 'Until more information is available, caution is advised when ospemifene is used concomitantly with drugs that are substrates of OCT1, UGT1A3, and/or UGT1A9, particularly those with a narrow therapeutic range. Patients should be monitored for the development of adverse effects.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20456/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[3] "Product Information. Osphena (ospemifene)." Shionogi USA Inc, Florham Park, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20442, 'Ambrisentan', 'Pegaspargase', 'Moderate', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury. Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults. Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins). Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs. Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20457/', '[1] "Product Information. Oncaspar (pegaspargase)." Rhone-Poulenc Rorer, Collegeville, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Bodmer M, Sulz M, Stadlmann S, Droll A, Terracciano L, Krahenbuhl S "Fatal liver failure in an adult pati