INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20472, 'Amifampridine', 'Ambenonium', 'Moderate', 'Coadministration of amifampridine with direct or indirect cholinesterase inhibitors may result in additive cholinergic effects and increased adverse reactions such as nausea, diarrhea, abdominal pain, muscle weakness, dizziness, drowsiness, and seizures. Amifampridine may produce cholinergic effects by facilitating the release of acetylcholine from nerve endings and enhancing neuromuscular transmission.', NULL, 'Patients should be apprised of the potential for increased cholinergic effects when amifampridine is coadministered with other drugs that have cholinergic effects such as cholinesterase inhibitors.', NULL, 'Patients should be apprised of the potential for increased cholinergic effects when amifampridine is coadministered with other drugs that have cholinergic effects such as cholinesterase inhibitors.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20487/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] "Product Information. Firdapse (amifampridine)." Catalyst Pharmaceuticals, Inc., Coral Gables, FL.', 1767365203);
