INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20757, 'Ampicillin', 'Amikacin', 'Moderate', 'INTERVAL: Ampicillin may inactivate aminoglycosides in vivo and in vitro. The mechanism is complexation with the aminoglycoside. Typically, this effect is significant only in patients with renal failure or if the agents are mixed in the same IV container or line. Lower serum aminoglycoside levels may be seen, and dosage adjustments may be necessary.', NULL, 'Ampicillin and aminoglycosides should be administered separately during combined therapy.', NULL, 'Ampicillin and aminoglycosides should be administered separately during combined therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20772/', '[1] Pickering LK, Rutherford I "Effect of concentration and time upon inactivation of tobramycin, gentamicin, netilmicin and amikacin by azlocillin, carbenicillin, mecillinam, mezlocillin and piperacillin." J Pharmacol Exp Ther 217 (1981): 345-9[2] Russo ME, Atkin-Thor E "Gentamicin and ticarcillin in subjects with end-stage renal disease." Clin Nephrol 15 (1981): 175-80[3] Viollier AF, Standiford HC, Drusano GL, Tatem BA, Moody MR, Schimpff SC "Comparative pharmacokinetics and serum bactericidal activity of mezlocillin, ticarcillin and piperacillin, with and without gentamicin." J Antimicrob Chemother 15 (1985): 597-606[4] Kradjan WA, Burger R "In vivo inactivation of gentamycin by carbenicillin and ticarcillin." Arch Intern Med 140 (1980): 1668-70[5] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[6] Halstenson CE, Hirata CA, Heim-Duthoy KL, Abraham PA, Matzke GR "Effect of concomitant administration of