INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20789, 'Amikacin', 'Choline salicylate', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20804/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20790, 'Amikacin', 'Cidofovir', 'Major', 'Coadministration of cidofovir with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney. Dose-related nephrotoxicity is the major toxicity of cidofovir. Cases of acute renal failure resulting in dialysis and/or contributing to death have occurred with as few as one or two doses of cidofovir.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, cisplatin, clofarabine, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravascular contrast media; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20805/', '[1] "Product Information. Vistide (cidofovir)." Gilead Sciences, Foster City, CA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20791, 'Amikacin', 'Cisatracurium', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20806/', '[1] Jedeikin R, Dolgunski E, Kaplan R, Hoffman S "Prolongation of neuromuscular blocking effect of vecuronium by antibiotics." Anaesthesia 42 (1987): 858-60[2] Kronenfeld MA, Thomas SJ, Turndorf H "Recurrence of neuromuscular blockade after reversal of vecuronium in a patient receiving polymyxin/amikacin sternal irrigation." Anesthesiology 65 (1986): 93-4[3] Geha DG, Blitt CD, Moon BJ "Prolonged neuromuscular blockade with pancuronium in the presence of acute renal failure: a case report." Anesth Analg 55 (1976): 343-5[4] Giala MM, Paradelis AG "Two cases of prolonged respiratory depression due to interaction of pancuronium with colistin and streptomycin." J Antimicrob Chemother 5 (1979): 234-5[5] Regan AG, Perumbetti PP "Pancuronium and gentamicin interaction in patients with renal failure." Anesth Analg 59 (1980): 393[6] Pittinger CB, Eryasa Y, Adamson R "Antibiotic-induced paralysis." Anesth Analg 49 (1970): 487-501[7] Levanen J, Nordman R "Complete respiratory paralysis caused by a large dose of streptomycin and its treatment with calcium chloride." Ann Clin Res 7 (1975): 47-9[8] "Product Information. Humatin (paromomycin)." Parke-Davis, Morris Plains, NJ.[9] Warner WA, Sanders E "Neuromuscular blockade associated with gentamicin therapy." JAMA 215 (1971): 1153-4[10] Vanacker BF, Van de Walle J "The neuromuscular blocking action of vecuronium in normal patients and in patients with no renal function and interaction vecuronium- tobramycin in renal transplant patients." Acta Anaesthesiol Belg 37 (1986): 95-9[11] Dupuis JY, Martin R, Tetrault JP "Atracurium and vecuronium interaction with gentamicin and tobramycin." Can J Anaesth 36 (1989): 407-11[12] Chapple DJ, Clark JL, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl (1983): s17-22[13] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[14] Burkett L, Bikhazi GB, Thomas KC Jr, Rosenthal DA, Wirta MG, Foldes FF "Mutual potentiation of the neuromuscular effects of antibiotics and relaxants." Anesth Analg 58 (1979): 107-15[15] Lippmann M, Yang E, Au E, Lee C "Neuromuscular blocking effects of tobramycin, gentamicin, and cefazolin." Anesth Analg 61 (1982): 767-70[16] Hasfurther DL, Bailey PL "Failure of neuromuscular blockade reversal after rocuronium in a patient who received oral neomycin." Can J Anaesth 43 (1996): 617-20[17] Giala M, Sareyiannis C, Cortsaris N, Paradelis A, Lappas DG "Possible interaction of pancuronium and tubocurarine with oral neomycin." Anaesthesia 37 (1982): 776', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20792, 'Amikacin', 'Cisplatin', 'Moderate', 'Combining cisplatin and aminoglycosides may increase the risk of renal toxicity. Both cisplatin and aminoglycosides have been reported to cause renal magnesium wasting. The exact mechanism of the interaction is unknown.', NULL, 'In patients currently receiving cisplatin therapy, the use of less nephrotoxic antimicrobials is preferable.', NULL, 'In patients currently receiving cisplatin therapy, the use of less nephrotoxic antimicrobials is preferable. If cisplatin and an aminoglycoside must be used together, renal function and serum magnesium should be monitored carefully.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20807/', '[1] Hass A, Anderson L, Lad T "The influence of aminoglycosides on the nephrotoxicity of cis-diamminedichlorophatinum in cancer patients." J Infect Dis 147 (1983): 363[2] Blachley JD, Hill JB "Renal and electrolyte disturbances associated with cisplatin." Ann Intern Med 95 (1981): 628-32[3] Dentino M, Luft FC, Yum MN, et al "Long term effect of cis-diamminedichloride platinum (CDDP) on renal function and structure in man." Cancer 41 (1978): 1274-81[4] Stewart CF, Christensen ML, Crom WR, Evans WE "The effect of cisplatin therapy on gentamicin pharmacokinetics." Drug Intell Clin Pharm 18 (1984): 512[5] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[6] Christensen ML, Stewart CF, Crom WR "Evaluation of aminoglycoside disposition in patients previously treated with cisplatin." Ther Drug Monit 11 (1989): 631-6[7] Patel R, Savage A "Symptomatic hypomagnesemia associated with gentamicin therapy." Nephron 23 (1979): 50-2', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20793, 'Amikacin', 'Clofarabine', 'Moderate', 'Coadministration of clofarabine with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney. Moreover, renal impairment secondary to the use of these agents may reduce the clearance of clofarabine, which is primarily eliminated by renal excretion. This may increase the risk of other adverse effects such as nausea, vomiting, stomatitis, hypertension, hypotension, myelosuppression, hemorrhage, and hepatotoxicity.', NULL, 'Drugs that are potentially nephrotoxic (e.', NULL, 'Drugs that are potentially nephrotoxic (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, cidofovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, carboplatin, cisplatin, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) should be avoided during the 5 days of clofarabine administration if possible. Renal function should be evaluated prior to and during therapy, and administration discontinued immediately if substantial increases (e.g., grade 3 or higher) in creatinine are noted.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20808/', '[1] "Product Information. Clolar (clofarabine)." sanofi-aventis, Bridgewater, NJ.', 1767363020);
