INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20814, 'Gentamicin', 'Amikacin', 'Moderate', 'Use of more than one aminoglycoside concurrently or sequentially may increase the risk of nephrotoxicity and ototoxicity. The risk is greatest in patients with impaired renal function, those receiving high doses for prolonged periods of time, elderly patients, and dehydrated patients. Ototoxicity may occur without renal toxicity. Oral neomycin undergoes systemic absorption and has also been associated with nephrotoxicity and ototoxicity.', NULL, 'The use of more than one aminoglycoside should be avoided.', NULL, 'The use of more than one aminoglycoside should be avoided. Patients receiving aminoglycosides should be closely monitored for development of toxicity, including renal function tests and serial, vestibular, and audiometric tests. Ototoxicity may be permanent and may not be apparent until long after therapy has been discontinued. The onset of auditory toxicity may be asymptomatic or may manifest with early symptoms such as feelings of fullness in the ear, high tone hearing loss, or tinnitus. Signs of vestibulotoxicity include ataxia, tinnitus, and vertigo.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20829/', '[1] Lawson DH, Tilstone WJ, Gray JM, Srivastava PK "Effect of furosemide on the pharmacokinetics of gentamicin in patients." J Clin Pharmacol 22 (1982): 254-8[2] Kaka JS, Lyman C, Kilarski DJ "Tobramycin-furosemide interaction." Drug Intell Clin Pharm 18 (1984): 235-8[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Johnson AH, Hamilton CH "Kanamycin ototoxicity: possible potentiation by other drugs." South Med J 63 (1970): 511-3[6] Weibert R, Keane W, Shapiro F "Carbenicillin inactivation of aminoglycosides in patients with severe renal failure." Trans Am Soc Artif Intern Organs 22 (1976): 439-43[7] Kaka JS, Lyman C, Kilarski DJ "Tobramycin-furosemide interaction." Drug Intell Clin Pharm 18 (1984): 235-8[8] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[9] Dupuis JY, Martin R, Tetrault JP "Atracurium and vecuronium interaction with gentamicin and tobramycin." Can J Anaesth 36 (1989): 407-11[10] "Product Information. Garamycin (gentamicin)." Schering-Plough Corporation, Kenilworth, NJ.[11] "Product Information. Mycifradin (neomycin)." Emerson Laboratories, Texarkana, TX.[12] "Product Information. Nebcin (tobramycin)." Lilly, Eli and Company, Indianapolis, IN.[13] Mathog RH, Klein WJ "Ototoxicity of ethacrynic acid and aminoglycoside antibiotics in uremia." N Engl J Med 280 (1969): 1223-4[14] Halstenson CE, Hirata CA, Heim-Duthoy KL, Abraham PA, Matzke GR "Effect of concomitant administration of piperacillin on the dispositions of netilmicin and tobramycin in patients with end-stage renal disease." Antimicrob Agents Chemother 34 (1990): 128-33', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20815, 'Gentamicin (topical)', 'Amikacin', 'Moderate', 'Topically applied aminoglycosides may be systemically absorbed and have potentially additive effects with systemically administered aminoglycosides. The risk of oto- and nephrotoxicity and other adverse effects of aminoglycosides may be increased.', NULL, 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.', NULL, 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.g., application to burnt or broken skin; use of large quantities or on large areas for prolonged periods). The normally recommended dosages of these products should not be exceeded.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20830/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20816, 'Givosiran', 'Amikacin', 'Moderate', 'The concurrent or sequential use of two or more potentially nephrotoxic or neurotoxic drugs may increase the risk of toxicity. Patients who are elderly, debilitated, dehydrated, or who have preexisting renal dysfunction may have a higher risk of developing adverse reactions.', NULL, 'Close clinical and laboratory monitoring of renal and neurologic function and drug serum concentrations (if clinically appropriate) is recommended.', NULL, 'Close clinical and laboratory monitoring of renal and neurologic function and drug serum concentrations (if clinically appropriate) is recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20831/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20817, 'Ibandronate', 'Amikacin', 'Moderate', 'Coadministration of bisphosphonates and aminoglycosides may potentiate the risks of hypocalcemia and nephrotoxicity, particularly when the bisphosphonate is administered intravenously. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium, while aminoglycosides can induce renal tubular damage resulting in renal loss of calcium and magnesium. Hypomagnesemia, in turn, may also lead to peripheral parathyroid hormone resistance and impaired parathyroid hormone secretion, thus compromising the normal homeostatic response to hypocalcemia.', NULL, 'Caution is advised if bisphosphonates are used in patients who have recently received or are receiving treatment with aminoglycosides.', NULL, 'Caution is advised if bisphosphonates are used in patients who have recently received or are receiving treatment with aminoglycosides. Renal function as well as serum calcium and magnesium levels should be monitored during and after treatment, since renal loss of calcium and magnesium can continue for many weeks after cessation of aminoglycosides, as can the effect of bisphosphonates. Patients and clinicians should be alert to signs and symptoms of clinical hypocalcemia such as muscle spasms, carpopedal spasm, laryngeal spasm, tetany, convulsions, mental changes (irritability, depression, psychosis), increased intracranial pressure, papilledema, QT interval prolongation, and cardiac arrhythmias.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20832/', '[1] Chang JT, Green L, Beitz J "Renal failure with the use of zoledronic acid." N Engl J Med 349 (2003): 1676-9; discussion 1676-9[2] Kanis JA, Preston CJ, Yates AJ, Percival RC, Mundy KI, Russell RG "Effects of intravenous diphosphonates on renal function." Lancet 1 (1983): 1328[3] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Markowitz GS, Fine PL, Stack JI, et al. "Toxic acute tubular necrosis following treatment with zoledronate (Zometa)." Kidney Int 64 (2003): 281-289[5] Janssen Van Doorn K, Neyns B, Van Der Niepen P, Verbeelen D "Pamidronate-related nephrotoxicity (tubulointerstitial nephritis) in a patient with osteolytic bone metastases." Nephron 89 (2001): 467-8[6] "Product Information. Ostac (clodronate)." Hoffmann-La Roche Limited, Mississauga, IA.[7] Zazgornik J, Grafinger P, Biesenbach G, Hubmann R, Fridrik M "Acute renal failure and alendronate." Nephrol Dial Transplant 12 (1997): 2797-8[8] Banerjee D, Asif A, Striker L, Preston RA, Bourgoignie JJ, Roth D "Short-term, high-dose pamidronate-induced acute tubular necrosis: The postulated mechanisms of bisphosphonate nephrotoxicity." Am J Kidney Dis 41 (2003): E18[9] Osullivan TL, Akbari A, Cadnapaphornchai P "Acute renal failure associated with the administration of parenteral etidronate." Ren Fail 16 (1994): 767-73[10] Pedersen-Bjergaard U, Myhre J "Severe hypocalcaemia [corrected] after treatment with diphosphonate and aminoglycoside." Br Med J 302 (1991): 295[11] Lockridge L, Papac RJ, Perazella MA "Pamidronate-associated nephrotoxicity in a patient with Langerhans''s histiocytosis." Am J Kidney Dis 40 (2002): E2[12] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767363020);
