INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20865, 'Amikacin', 'Piroxicam', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20880/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20866, 'Amikacin', 'Plazomicin', 'Moderate', 'Use of more than one aminoglycoside concurrently or sequentially may increase the risk of nephrotoxicity and ototoxicity. The risk is greatest in patients with impaired renal function, those receiving high doses for prolonged periods of time, elderly patients, and dehydrated patients. Ototoxicity may occur without renal toxicity. Oral neomycin undergoes systemic absorption and has also been associated with nephrotoxicity and ototoxicity.', NULL, 'The use of more than one aminoglycoside should be avoided.', NULL, 'The use of more than one aminoglycoside should be avoided. Patients receiving aminoglycosides should be closely monitored for development of toxicity, including renal function tests and serial, vestibular, and audiometric tests. Ototoxicity may be permanent and may not be apparent until long after therapy has been discontinued. The onset of auditory toxicity may be asymptomatic or may manifest with early symptoms such as feelings of fullness in the ear, high tone hearing loss, or tinnitus. Signs of vestibulotoxicity include ataxia, tinnitus, and vertigo.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20881/', '[1] Lawson DH, Tilstone WJ, Gray JM, Srivastava PK "Effect of furosemide on the pharmacokinetics of gentamicin in patients." J Clin Pharmacol 22 (1982): 254-8[2] Kaka JS, Lyman C, Kilarski DJ "Tobramycin-furosemide interaction." Drug Intell Clin Pharm 18 (1984): 235-8[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Johnson AH, Hamilton CH "Kanamycin ototoxicity: possible potentiation by other drugs." South Med J 63 (1970): 511-3[6] Weibert R, Keane W, Shapiro F "Carbenicillin inactivation of aminoglycosides in patients with severe renal failure." Trans Am Soc Artif Intern Organs 22 (1976): 439-43[7] Kaka JS, Lyman C, Kilarski DJ "Tobramycin-furosemide interaction." Drug Intell Clin Pharm 18 (1984): 235-8[8] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[9] Dupuis JY, Martin R, Tetrault JP "Atracurium and vecuronium interaction with gentamicin and tobramycin." Can J Anaesth 36 (1989): 407-11[10] "Product Information. Garamycin (gentamicin)." Schering-Plough Corporation, Kenilworth, NJ.[11] "Product Information. Mycifradin (neomycin)." Emerson Laboratories, Texarkana, TX.[12] "Product Information. Nebcin (tobramycin)." Lilly, Eli and Company, Indianapolis, IN.[13] Mathog RH, Klein WJ "Ototoxicity of ethacrynic acid and aminoglycoside antibiotics in uremia." N Engl J Med 280 (1969): 1223-4[14] Halstenson CE, Hirata CA, Heim-Duthoy KL, Abraham PA, Matzke GR "Effect of concomitant administration of piperacillin on the dispositions of netilmicin and tobramycin in patients with end-stage renal disease." Antimicrob Agents Chemother 34 (1990): 128-33', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20867, 'Amikacin', 'Polymyxin B', 'Major', 'The combination of a polypeptide antibiotic and an aminoglycoside may increase the risk of neurotoxicity, respiratory paralysis, and renal dysfunction. The mechanism of this interaction is not known, but may involve additive or synergistic effects.', NULL, 'This combination should be avoided concurrently or sequentially.', NULL, 'This combination should be avoided concurrently or sequentially. If coadministration is medically necessary, patients should be closely monitored for development of toxicity, including renal function tests, respiratory function, and vestibular and audiometric tests.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20882/', '[1] "Product Information. Garamycin (gentamicin)." Schering-Plough Corporation, Kenilworth, NJ.[2] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[3] Lindesmith LA, Baines RD Jr, Bigelow DB, Petty TL "Reversible respiratory paralysis associated with polymyxin therapy." Ann Intern Med 68 (1968): 318-27[4] Lee C, de Silva AJ "Interaction of neuromuscular blocking effects of neomycin and polymyxin B." Anesthesiology 50 (1979): 218-20[5] "Product Information. Nebcin (tobramycin)." Lilly, Eli and Company, Indianapolis, IN.[6] "Product Information. Amikin (amikacin)." Bristol-Myers Squibb, Princeton, NJ.[7] Koch-Weser J, Sidel VW, Federman EB, Kanarek P, Finer DC, Eaton AE "Adverse effects of sodium colistimethate: manifestations and specific reaction rates during 317 courses of therapy." Ann Intern Med 72 (1970): 857-68', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20868, 'Amikacin', 'Pyridostigmine', 'Moderate', 'Aminoglycosides may antagonize the effects of anticholinesterase agents such as neostigmine and pyridostigmine. The interaction has been established between aminoglycosides and depolarizing and nondepolarizing muscle relaxants and is also predicted to occur with anticholinesterase agents. Anticholinesterase agents inhibit cholinesterase activity and lead to prolonged and intensified muscarinic and nicotinic effects of acetylcholine. Aminoglycosides appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. They also possess neuromuscular blocking activity, which may interfere with neuromuscular transmission by inhibiting the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. This effect on neuromuscular transmission may be particularly significant in patients with myasthenia gravis.', NULL, 'Caution and close monitoring for the efficacy of the anticholinesterase agent is recommended if it is to be used concomitantly with aminoglycosides, particularly in patients with myasthenia gravis.', NULL, 'Caution and close monitoring for the efficacy of the anticholinesterase agent is recommended if it is to be used concomitantly with aminoglycosides, particularly in patients with myasthenia gravis. Concomitant use in these patients should be avoided if possible. If coadministration is considered necessary, the dose of the anticholinesterase agent may need to be increased.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20883/', '[1] Levanen J, Nordman R "Complete respiratory paralysis caused by a large dose of streptomycin and its treatment with calcium chloride." Ann Clin Res 7 (1975): 47-9[2] Pittinger CB, Eryasa Y, Adamson R "Antibiotic-induced paralysis." Anesth Analg 49 (1970): 487-501[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Giala MM, Paradelis AG "Two cases of prolonged respiratory depression due to interaction of pancuronium with colistin and streptomycin." J Antimicrob Chemother 5 (1979): 234-5[5] Geha DG, Blitt CD, Moon BJ "Prolonged neuromuscular blockade with pancuronium in the presence of acute renal failure: a case report." Anesth Analg 55 (1976): 343-5[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Hasfurther DL, Bailey PL "Failure of neuromuscular blockade reversal after rocuronium in a patient who received oral neomycin." Can J Anaesth 43 (1996): 617-20[8] Warner WA, Sanders E "Neuromuscular blockade associated with gentamicin therapy." JAMA 215 (1971): 1153-4[9] Chapple DJ, Clark JL, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl (1983): s17-22[10] Burkett L, Bikhazi GB, Thomas KC Jr, Rosenthal DA, Wirta MG, Foldes FF "Mutual potentiation of the neuromuscular effects of antibiotics and relaxants." Anesth Analg 58 (1979): 107-15[11] Lippmann M, Yang E, Au E, Lee C "Neuromuscular blocking effects of tobramycin, gentamicin, and cefazolin." Anesth Analg 61 (1982): 767-70[12] Kronenfeld MA, Thomas SJ, Turndorf H "Recurrence of neuromuscular blockade after reversal of vecuronium in a patient receiving polymyxin/amikacin sternal irrigation." Anesthesiology 65 (1986): 93-4[13] Dupuis JY, Martin R, Tetrault JP "Atracurium and vecuronium interaction with gentamicin and tobramycin." Can J Anaesth 36 (1989): 407-11[14] Jedeikin R, Dolgunski E, Kaplan R, Hoffman S "Prolongation of neuromuscular blocking effect of vecuronium by antibiotics." Anaesthesia 42 (1987): 858-60[15] Regan AG, Perumbetti PP "Pancuronium and gentamicin interaction in patients with renal failure." Anesth Analg 59 (1980): 393[16] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[17] "Product Information. Humatin (paromomycin)." Parke-Davis, Morris Plains, NJ.[18] Giala M, Sareyiannis C, Cortsaris N, Paradelis A, Lappas DG "Possible interaction of pancuronium and tubocurarine with oral neomycin." Anaesthesia 37 (1982): 776[19] Vanacker BF, Van de Walle J "The neuromuscular blocking action of vecuronium in normal patients and in patients with no renal function and interaction vecuronium- tobramycin in renal transplant patients." Acta Anaesthesiol Belg 37 (1986): 95-9', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20869, 'Amikacin', 'Rapacuronium', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20884/', '[1] Jedeikin R, Dolgunski E, Kaplan R, Hoffman S "Prolongation of neuromuscular blocking effect of vecuronium by antibiotics." Anaesthesia 42 (1987): 858-60[2] Kronenfeld MA, Thomas SJ, Turndorf H "Recurrence of neuromuscular blockade after reversal of vecuronium in a patient receiving polymyxin/amikacin sternal irrigation." Anesthesiology 65 (1986): 93-4[3] Geha DG, Blitt CD, Moon BJ "Prolonged neuromuscular blockade with pancuronium in the presence of acute renal failure: a case report." Anesth Analg 55 (1976): 343-5[4] Giala MM, Paradelis AG "Two cases of prolonged respiratory depression due to interaction of pancuronium with colistin and streptomycin." J Antimicrob Chemother 5 (1979): 234-5[5] Regan AG, Perumbetti PP "Pancuronium and gentamicin interaction in patients with renal failure." Anesth Analg 59 (1980): 393[6] Pittinger CB, Eryasa Y, Adamson R "Antibiotic-induced paralysis." Anesth Analg 49 (1970): 487-501[7] Levanen J, Nordman R "Complete respiratory paralysis caused by a large dose of streptomycin and its treatment with calcium chloride." Ann Clin Res 7 (1975): 47-9[8] "Product Information. Humatin (paromomycin)." Parke-Davis, Morris Plains, NJ.[9] Warner WA, Sanders E "Neuromuscular blockade associated with gentamicin therapy." JAMA 215 (1971): 1153-4[10] Vanacker BF, Van de Walle J "The neuromuscular blocking action of vecuronium in normal patients and in patients with no renal function and interaction vecuronium- tobramycin in renal transplant patients." Acta Anaesthesiol Belg 37 (1986): 95-9[11] Dupuis JY, Martin R, Tetrault JP "Atracurium and vecuronium interaction with gentamicin and tobramycin." Can J Anaesth 36 (1989): 407-11[12] Chapple DJ, Clark JL, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl (1983): s17-22[13] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[14] Burkett L, Bikhazi GB, Thomas KC Jr, Rosenthal DA, Wirta MG, Foldes FF "Mutual potentiation of the neuromuscular effects of antibiotics and relaxants." Anesth Analg 58 (1979): 107-15[15] Lippmann M, Yang E, Au E, Lee C "Neuromuscular blocking effects of tobramycin, gentamicin, and cefazolin." Anesth Analg 61 (1982): 767-70[16] Hasfurther DL, Bailey PL "Failure of neuromuscular blockade reversal after rocuronium in a patient who received oral neomycin." Can J Anaesth 43 (1996): 617-20[17] Giala M, Sareyiannis C, Cortsaris N, Paradelis A, Lappas DG "Possible interaction of pancuronium and tubocurarine with oral neomycin." Anaesthesia 37 (1982): 776', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20870, 'Amikacin', 'Remdesivir', 'Moderate', 'Coadministration of remdesivir with nephrotoxic agents may increase the plasma concentrations of its main metabolite, GS-441524, and the excipient, sulfobutylether-beta-cyclodextrin sodium (SBECD). Both GS-441524 and SBECD are primarily eliminated by the kidneys, thus renal impairment secondary to the use of nephrotoxic agents may reduce their clearance and increase the risk of accumulation.', NULL, 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function.', NULL, 'Some authorities recommend avoiding the concomitant use of remdesivir with drugs that can reduce renal function. Caution is advised if remdesivir is used in patients who have recently received potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function (estimated glomerular filtration rate, or eGFR, for adult and pediatric patients older than 28 days; serum creatinine for full-term neonates at least 7 days up to 28 days old) should be evaluated prior to starting remdesivir and monitored daily during treatment or as clinically appropriate.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20885/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[5] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20871, 'Human Rho(D) immune globulin', 'Amikacin', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20886/', '[1] MMWR Morb Mortal Wkly Rep "Renal insufficiency and failure associated with immune globulin intravenous therapy -- United States, 1985-1998. Available from: URL: http://www.cdc.gov/mmwr/preview/mmwrhtml/mm4824a3.htm." ([1999 Jun 25]):[2] "Product Information. BabyBIG (botulism immune globulin)." FFF Enterprises, Temecula, CA.[3] Guo X, Nzerue C "How to prevent, recognize, and treat drug-induced nephrotoxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2002 Apr]):[4] Moses S "Nephrotoxic drugs, drug-induced nephrotoxicity. Available from: URL: http://www.fpnotebook.com/Renal/Pharm/NphrtxcDrgs.htm." ([2011 Nov 25]):[5] Perazella MA "Renal vulnerability to drug toxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2009]):[6] Levy JB, Pusey EY "Nephrotoxicity of intravenous immunoglobulin. Available from: URL: http://qjmed.oxfordjournals.org/content/93/11/751.full.pdf+html." ([2000]):[7] "Product Information. Respigam (respiratory syncytial virus immune globulin)." Medimmune Inc, Gaithersburg, MD.[8] "Product Information. Bivigam (immune globulin intravenous)." Biotest Pharmaceuticals Corporation, Boca Raton, FL.[9] "Product Information. Cytogam (cytomegalovirus immune globulin)." CSL Behring, King of Prussia, PA.[10] Naughton CA "Drug-induced nephrotoxicity. Available from: URL: http://www.aafp.org/afp/2008/0915/p743.html." ([2008]):[11] KDIGO. Kidney Disease Improving Global Outcomes "KDIGO clinical practice guideline for acute kidney injury. Available from: URL: http://www.kdigo.org/clinical_practice_guidelines/pdf/KDIGO-AKI-Suppl-Appendices-A-F_March2012.pdf." ([2012 Mar]):', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20872, 'Amikacin', 'Botulinum toxin type B', 'Major', 'The effects of botulinum toxin may be potentiated by neuromuscular blockers, aminoglycoside antibiotics, and other drugs that can interfere with neuromuscular transmission. Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive neuromuscular blocking effects may occur when these agents are used together.', NULL, 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission.', NULL, 'Caution is advised when botulinum toxin is used in combination with neuromuscular blockers, aminoglycoside antibiotics, or other drugs that can interfere with neuromuscular transmission. These drugs should be avoided in patients suspected of having botulinum toxin overdose or poisoning. Because the effects of botulinum toxin products may spread from the area of injection, patients should be monitored closely for adverse effects such as asthenia, generalized muscle weakness, diplopia, blurred vision, ptosis, dysphagia, dysphonia, dysarthria, urinary incontinence and breathing difficulties, which can occur hours to weeks after injection.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20887/', '[1] "Product Information. Myobloc (botulinum toxin type B)" Elan Pharmaceuticals, S. San Francisco, CA.[2] "Product Information. Dysport (abobotulinumtoxinA)." Tercica Inc, Brisbane, CA.[3] "Product Information. Jeuveau (prabotulinumtoxinA)." Evolus, Inc., Santa Barbara, CA.[4] "Product Information. Botox (onabotulinumtoxin A)." Allergan Inc, Irvine, CA.[5] "Product Information. Xeomin (botulinum toxin type A (obsolete) (botulinum toxin type A))." Merz Pharmaceuticals, Greensboro, NC.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20873, 'Amikacin', 'Risedronic acid', 'Moderate', 'Coadministration of bisphosphonates and aminoglycosides may potentiate the risks of hypocalcemia and nephrotoxicity, particularly when the bisphosphonate is administered intravenously. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium, while aminoglycosides can induce renal tubular damage resulting in renal loss of calcium and magnesium. Hypomagnesemia, in turn, may also lead to peripheral parathyroid hormone resistance and impaired parathyroid hormone secretion, thus compromising the normal homeostatic response to hypocalcemia.', NULL, 'Caution is advised if bisphosphonates are used in patients who have recently received or are receiving treatment with aminoglycosides.', NULL, 'Caution is advised if bisphosphonates are used in patients who have recently received or are receiving treatment with aminoglycosides. Renal function as well as serum calcium and magnesium levels should be monitored during and after treatment, since renal loss of calcium and magnesium can continue for many weeks after cessation of aminoglycosides, as can the effect of bisphosphonates. Patients and clinicians should be alert to signs and symptoms of clinical hypocalcemia such as muscle spasms, carpopedal spasm, laryngeal spasm, tetany, convulsions, mental changes (irritability, depression, psychosis), increased intracranial pressure, papilledema, QT interval prolongation, and cardiac arrhythmias.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20888/', '[1] Chang JT, Green L, Beitz J "Renal failure with the use of zoledronic acid." N Engl J Med 349 (2003): 1676-9; discussion 1676-9[2] Kanis JA, Preston CJ, Yates AJ, Percival RC, Mundy KI, Russell RG "Effects of intravenous diphosphonates on renal function." Lancet 1 (1983): 1328[3] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Markowitz GS, Fine PL, Stack JI, et al. "Toxic acute tubular necrosis following treatment with zoledronate (Zometa)." Kidney Int 64 (2003): 281-289[5] Janssen Van Doorn K, Neyns B, Van Der Niepen P, Verbeelen D "Pamidronate-related nephrotoxicity (tubulointerstitial nephritis) in a patient with osteolytic bone metastases." Nephron 89 (2001): 467-8[6] "Product Information. Ostac (clodronate)." Hoffmann-La Roche Limited, Mississauga, IA.[7] Zazgornik J, Grafinger P, Biesenbach G, Hubmann R, Fridrik M "Acute renal failure and alendronate." Nephrol Dial Transplant 12 (1997): 2797-8[8] Banerjee D, Asif A, Striker L, Preston RA, Bourgoignie JJ, Roth D "Short-term, high-dose pamidronate-induced acute tubular necrosis: The postulated mechanisms of bisphosphonate nephrotoxicity." Am J Kidney Dis 41 (2003): E18[9] Osullivan TL, Akbari A, Cadnapaphornchai P "Acute renal failure associated with the administration of parenteral etidronate." Ren Fail 16 (1994): 767-73[10] Pedersen-Bjergaard U, Myhre J "Severe hypocalcaemia [corrected] after treatment with diphosphonate and aminoglycoside." Br Med J 302 (1991): 295[11] Lockridge L, Papac RJ, Perazella MA "Pamidronate-associated nephrotoxicity in a patient with Langerhans''s histiocytosis." Am J Kidney Dis 40 (2002): E2[12] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20874, 'Amikacin', 'Rocuronium', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', NULL, 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20889/', '[1] Jedeikin R, Dolgunski E, Kaplan R, Hoffman S "Prolongation of