INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20986, 'Garlic', 'Amiloride', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21001/', '[1] Tattelman E "Health effects of garlic." Am Fam Physician 72 (2005): 103-6[2] Richard CL, Jurgens TM "Effects of natural health products on blood pressure." Ann Pharmacother 39 (2005): 712-20', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20987, 'Glycerin', 'Amiloride', 'Moderate', 'The chronic use or abuse of laxatives may potentiate the pharmacologic effects of diuretics. Laxatives can cause significant losses of fluid and electrolytes, including sodium, potassium, magnesium and zinc, and these effects may be additive to those of diuretics.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', NULL, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant use with diuretics, patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21002/', '[1] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[2] Blumenthal M, Goldberg A, Brinckmann J, eds. "Herbal Medicine: Expanded Commission E Monographs." Newton, MA: Integrative Medicine Communications (2000):[3] Atsmon J, Dolev E "Drug-induced hypomagnesaemia : scope and management." Drug Saf 28 (2005): 763-88[4] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8[5] Leary WP, Reyes AJ "Drug interactions with diuretics." S Afr Med J 65 (1984): 455-61', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (20988, 'Guanfacine', 'Amiloride', 'Moderate', 'Use of guanfacine in combination with antihypertensive agents may result in additive pharmacodynamic effects such as hypotension and syncope.', NULL, 'Caution is advised during coadministration of these agents.', NULL, 'Caution is advised during coadministration of these agents. Dosage adjustments may be required and hemodynamic responses should be monitored during concomitant therapy. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21003/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Tenex (guanfacine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.', 1767363020);
