INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336106, 'Ethanol', 'Acetohexamide', 'Moderate', 'Alcohol may cause hypoglycemia or hyperglycemia in patients with diabetes. Hypoglycemia most frequently occurs during acute consumption of alcohol. Even modest amounts can lower blood sugar significantly, especially when the alcohol is ingested on an empty stomach or following exercise. The mechanism involves inhibition of both gluconeogenesis as well as the counter-regulatory response to hypoglycemia.', 'DDInter', 1767317475, 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.', 'Others', 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336107, 'Ethanol', 'Alfentanil', 'Moderate', 'Ethanol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics. Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills. In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'DDInter', 1767317475, 'Concomitant use of opioid analgesics with ethanol should be avoided.', 'Synergism', 'Concomitant use of opioid analgesics with ethanol should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336108, 'Ethanol', 'Alfuzosin', 'Moderate', 'The concurrent use of ethanol and alpha-1 adrenergic blockers may cause increased hypotensive effects. Patients with aldehyde dehydrogenase deficiencies (primarily Asians) may be at a higher risk of this interaction. The mechanism has not been determined. Data exist for prazosin and other alpha adrenergic blockers are expected to interact also. In addition, any patients taking alpha adrenergic blockers may experience excessive orthostatic hypotension with ethanol ingestion, due to ethanol''s unopposed vasodilatory effects in the presence of alpha adrenergic blockade.', 'DDInter', 1767317475, 'Patients who develop a flushing reaction after ethanol ingestion (indicates a possible aldehyde dehydrogenase deficiency) should be advised to avoid ethanol or limit their intake. All patients should be warned about the possibility of orthostatic hypotension with concurrent ethanol use.', 'Synergism', 'Patients who develop a flushing reaction after ethanol ingestion (indicates a possible aldehyde dehydrogenase deficiency) should be advised to avoid ethanol or limit their intake.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336109, 'Ethanol', 'Alogliptin', 'Moderate', 'Alcohol may cause hypoglycemia or hyperglycemia in patients with diabetes. Hypoglycemia most frequently occurs during acute consumption of alcohol. Even modest amounts can lower blood sugar significantly, especially when the alcohol is ingested on an empty stomach or following exercise. The mechanism involves inhibition of both gluconeogenesis as well as the counter-regulatory response to hypoglycemia.', 'DDInter', 1767317475, 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.', 'Others', 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336110, 'Ethanol', 'Alprazolam', 'Moderate', 'Acute ethanol ingestion may potentiate the CNS effects of many benzodiazepines. Tolerance may develop with chronic ethanol use. The mechanism may be decreased clearance of the benzodiazepines because of CYP450 hepatic enzyme inhibition. Also, it has been suggested that the cognitive deficits induced by benzodiazepines may be increased in patients who chronically consume large amounts of alcohol.', 'DDInter', 1767317475, 'Patients should be advised to avoid alcohol during benzodiazepine therapy.', 'Metabolism', 'Patients should be advised to avoid alcohol during benzodiazepine therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336111, 'Ethanol', 'Amiloride', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336112, 'Amitriptyline', 'Ethanol', 'Moderate', 'Concomitant use of ethanol and a tricyclic antidepressant (TCA) may result altered TCA plasma levels and efficacy, and additive impairment of motor skills, especially driving skills. Acute ethanol ingestion may inhibit TCA metabolism, while chronic ingestion of large amounts of ethanol may induce hepatic TCA metabolism.', 'DDInter', 1767317475, 'Patients should be advised to avoid alcohol during TCA therapy. Alcoholics who have undergone detoxification should be monitored for decreased TCA efficacy. Dosage adjustments may be required.', 'Metabolism', 'Patients should be advised to avoid alcohol during TCA therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336113, 'Amobarbital', 'Ethanol', 'Major', 'Concurrent acute use of barbiturates and ethanol may result in additive CNS effects, including impaired coordination, sedation, and death. Tolerance of these agents may occur with chronic use. The mechanism is related to inhibition of microsomal enzymes acutely and induction of hepatic microsomal enzymes chronically.', 'DDInter', 1767317475, 'The combination of ethanol and barbiturates should be avoided.', 'Metabolism', 'The combination of ethanol and barbiturates should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
