INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336153, 'Ethanol', 'Buprenorphine', 'Major', 'Concomitant use of buprenorphine with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may increase the risk of buprenorphine overdose, severe respiratory depression, coma, and death.', 'DDInter', 1767317475, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. Extreme caution is advised when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol. Due to potential risk of overdose and death, dependence on sedative-hypnotics such as benzodiazepines or alcohol is considered a relative contraindication for office-based buprenorphine treatment of opioid addiction.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336154, 'Ethanol', 'Bupropion', 'Moderate', 'Excessive use or abrupt discontinuation of alcohol after chronic ingestion may precipitate seizures in patients receiving bupropion. Additionally, there have been rare post-marketing reports of adverse neuropsychiatric events or reduced alcohol tolerance in patients who drank alcohol during treatment with bupropion.', 'DDInter', 1767317475, 'The manufacturer recommends that alcohol consumption be minimized or avoided during bupropion treatment. The use of bupropion is contraindicated in patients undergoing abrupt discontinuation of alcohol.', 'Others', 'The manufacturer recommends that alcohol consumption be minimized or avoided during bupropion treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
