INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336160, 'Ethanol', 'Canagliflozin', 'Moderate', 'Alcohol may cause hypoglycemia or hyperglycemia in patients with diabetes. Hypoglycemia most frequently occurs during acute consumption of alcohol. Even modest amounts can lower blood sugar significantly, especially when the alcohol is ingested on an empty stomach or following exercise. The mechanism involves inhibition of both gluconeogenesis as well as the counter-regulatory response to hypoglycemia.', 'DDInter', 1767317475, 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.', 'Others', 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
