INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336194, 'Ethanol', 'Clomipramine', 'Moderate', 'The combination of ethanol and a tricyclic antidepressant may result in additive impairment of motor skills, especially driving skills. Also, one study has suggested that clomipramine metabolism is significantly impaired for several weeks or more following discontinuation of chronic alcohol consumption.', 'DDInter', 1767317475, 'Patients should be warned of this interaction and advised to limit their ethanol intake while taking tricyclic antidepressants. Monitoring for TCA toxicity (CNS depression, excessive anticholinergic effects, hypotension, arrhythmias) is recommended during alcohol withdrawal.', 'Others', 'Patients should be warned of this interaction and advised to limit their ethanol intake while taking tricyclic antidepressants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
