INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336223, 'Ethanol', 'Diroximel fumarate', 'Moderate', 'Coadministration of diroximel fumarate with ethanol may reduce the plasma concentrations of monomethyl fumarate (MMF), the active metabolite of diroximel fumarate. The mechanism has not been reported.', 'DDInter', 1767317475, 'The manufacturer recommends avoiding concomitant use of diroximel fumarate with ethanol.', 'Others', 'The manufacturer recommends avoiding concomitant use of diroximel fumarate with ethanol.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336224, 'Ethanol', 'Disopyramide', 'Minor', 'Ethanol significantly increases the renal elimination of disopyramide, apparently by inducing diuresis (inhibition of antidiuretic hormone). Limited data show that ethanol does not, however, significantly affect the elimination half-life or total plasma clearance of disopyramide. No special precautions appear to be necessary.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336225, 'Ethanol', 'Disulfiram', 'Major', 'Consumption of ethanol during treatment with disulfiram may cause flushing, nausea, blurred vision, dyspnea, tachypnea, tachycardia, and hypotension. Death has been reported. The mechanism is probably related to inhibition of aldehyde dehydrogenase, the enzyme responsible for the oxidation of acetaldehyde to acetyl CoA. Accumulation of acetaldehyde probably results.', 'DDInter', 1767317475, 'Ethanol should be avoided in patients receiving disulfiram.', 'Others', 'Ethanol should be avoided in patients receiving disulfiram.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336226, 'Ethanol', 'Valproic acid', 'Moderate', 'Alcohol may potentiate some of the pharmacologic effects of central nervous system (CNS)-active agents. Use in combination may result in additive CNS depression and/or impairment of judgment, thinking, and psychomotor skills.', 'DDInter', 1767317475, 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', 'Synergism', 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336227, 'Ethanol', 'Doxazosin', 'Moderate', 'The concurrent use of ethanol and alpha-1 adrenergic blockers may cause increased hypotensive effects. Patients with aldehyde dehydrogenase deficiencies (primarily Asians) may be at a higher risk of this interaction. The mechanism has not been determined. Data exist for prazosin and other alpha adrenergic blockers are expected to interact also. In addition, any patients taking alpha adrenergic blockers may experience excessive orthostatic hypotension with ethanol ingestion, due to ethanol''s unopposed vasodilatory effects in the presence of alpha adrenergic blockade.', 'DDInter', 1767317475, 'Patients who develop a flushing reaction after ethanol ingestion (indicates a possible aldehyde dehydrogenase deficiency) should be advised to avoid ethanol or limit their intake. All patients should be warned about the possibility of orthostatic hypotension with concurrent ethanol use.', 'Synergism', 'Patients who develop a flushing reaction after ethanol ingestion (indicates a possible aldehyde dehydrogenase deficiency) should be advised to avoid ethanol or limit their intake.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336228, 'Ethanol', 'Doxepin', 'Moderate', 'Concomitant use of ethanol and a tricyclic antidepressant (TCA) may result altered TCA plasma levels and efficacy, and additive impairment of motor skills, especially driving skills. Acute ethanol ingestion may inhibit TCA metabolism, while chronic ingestion of large amounts of ethanol may induce hepatic TCA metabolism.', 'DDInter', 1767317475, 'Patients should be advised to avoid alcohol during TCA therapy. Alcoholics who have undergone detoxification should be monitored for decreased TCA efficacy. Dosage adjustments may be required.', 'Metabolism', 'Patients should be advised to avoid alcohol during TCA therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
