INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336252, 'Ethanol', 'Ethionamide', 'Major', 'The concomitant use of protionamide with alcohol may enhance the central nervous system exciting effect of protionamide. The mechanism is not known. Excessive use of alcohol with another thiocarbamide derivative ethionamide, has been reported to precipitate a psychotic reaction.', 'DDInter', 1767317475, 'Consumption of alcohol during protionamide treatment should be avoided. The manufacturer of ethionamide also advises that excess alcohol consumption should be avoided during ethionamide therapy (US).', 'Synergism', 'Consumption of alcohol during protionamide treatment should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
