INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336254, 'Ethotoin', 'Ethanol', 'Moderate', 'Acute consumption of alcohol may increase plasma phenytoin levels. Chronic consumption of alcohol may decrease plasma phenytoin levels. The mechanism of this interaction is related to induction of phenytoin metabolism by ethanol during chronic administration. Other hydantoin derivatives may be similarly affected by ethanol.', 'DDInter', 1767317475, 'Patients receiving phenytoin therapy should be warned about the interaction between phenytoin and ethanol and they should be advised to notify their physician if they experience worsening of seizure control or symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, nausea, or ataxia.', 'Metabolism', 'Patients receiving phenytoin therapy should be warned about the interaction between phenytoin and ethanol and they should be advised to notify their physician if they experience worsening of seizure control or symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, nausea, or ataxia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336255, 'Etodolac', 'Ethanol', 'Moderate', 'The concurrent use of aspirin or nonsteroidal anti-inflammatory drugs (NSAIDs) and ethanol may lead to gastrointestinal (GI) blood loss. The mechanism may be due to a combined local effect as well as inhibition of prostaglandins leading to decreased integrity of the GI lining.', 'DDInter', 1767317475, 'Patients should be counseled on this potential interaction and advised to refrain from alcohol consumption while taking aspirin or NSAIDs.', 'Synergism', 'Patients should be counseled on this potential interaction and advised to refrain from alcohol consumption while taking aspirin or NSAIDs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336256, 'Etomidate', 'Ethanol', 'Moderate', 'Alcohol may potentiate some of the pharmacologic effects of central nervous system (CNS)-active agents. Use in combination may result in additive CNS depression and/or impairment of judgment, thinking, and psychomotor skills.', 'DDInter', 1767317475, 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', 'Synergism', 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336257, 'Exenatide', 'Ethanol', 'Moderate', 'Alcohol may cause hypoglycemia or hyperglycemia in patients with diabetes. Hypoglycemia most frequently occurs during acute consumption of alcohol. Even modest amounts can lower blood sugar significantly, especially when the alcohol is ingested on an empty stomach or following exercise. The mechanism involves inhibition of both gluconeogenesis as well as the counter-regulatory response to hypoglycemia.', 'DDInter', 1767317475, 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.', 'Others', 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336258, 'Ezogabine', 'Ethanol', 'Moderate', 'Coadministration with ethanol may increase the plasma concentrations of ezogabine.', 'DDInter', 1767317475, 'In general, alcohol consumption should be avoided or limited during treatment with CNS-depressant agents. Patients should be advised of the potential for increased dose-related adverse reactions of ezogabine (e.g., dizziness, somnolence, nausea, constipation, urinary retention, blurred vision, memory impairment, tremor) when taken with alcohol, and to avoid hazardous activities that require mental alertness and motor coordination until they know how the medication affects them. Ezogabine can be taken with or without food.', 'Others', 'In general, alcohol consumption should be avoided or limited during treatment with CNS-depressant agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
