INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336264, 'Flibanserin', 'Ethanol', 'Major', 'INTERVAL: Coadministration of flibanserin with alcohol may potentiate the risk of severe hypotension, syncope, and central nervous system depression.', 'DDInter', 1767317475, 'Use of flibanserin and alcohol together close in time increases the risk of severe hypotension and syncope. Patients should wait at least 2 hours after consuming 1 or 2 standard alcoholic drinks before taking their bedtime dose of flibanserin. Flibanserin should be taken at bedtime to minimize the risk of hypotension, syncope, accidental injury, and central nervous system depression. Patients who drink 3 or more standard alcoholic drinks should skip their flibanserin dose that evening. Alcohol should not be consumed until at least the morning after taking flibanserin at bedtime. A standard alcoholic drink contains 14 g of pure alcohol and is equivalent to one 12-ounce regular beer (5% alcohol), 5-ounces wine (12% alcohol), or 1.5 ounces of distilled spirits/shot (40% alcohol).', 'Synergism', 'Use of flibanserin and alcohol together close in time increases the risk of severe hypotension and syncope.', NULL, NULL, 'Major', 'pharmacodynamic');
