INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336285, 'Hydrochlorothiazide', 'Ethanol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336286, 'Hydrocodone', 'Ethanol', 'Major', 'Alcohol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics including hydrocodone. Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills. In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur. Consumption of alcohol while taking some sustained-release formulations of hydrocodone may cause rapid release of the drug, resulting in high systemic levels of hydrocodone that may be potentially lethal. Alcohol apparently can disrupt the release mechanism of some sustained-release formulations.', 'DDInter', 1767317475, 'Patients taking sustained-release formulations of hydrocodone should not consume alcohol or use medications that contain alcohol. In general, potent narcotics such as hydrocodone should not be combined with alcohol.', 'Synergism', 'Patients taking sustained-release formulations of hydrocodone should not consume alcohol or use medications that contain alcohol.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336287, 'Hydroflumethiazide', 'Ethanol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317475, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336288, 'Hydromorphone', 'Ethanol', 'Major', 'Alcohol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics including hydromorphone. Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills. In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur. Consumption of alcohol while taking sustained-release formulations of hydromorphone may cause rapid release of the drug, resulting in high systemic levels of hydromorphone that may be potentially lethal even in opioid-tolerant patients. Alcohol appears to disrupt the extended release mechanism, causing ''dose-dumping'' into the bloodstream.', 'DDInter', 1767317475, 'Patients taking sustained-release formulations of hydromorphone should not consume alcohol or use medications that contain alcohol on days of hydromorphone dosing. In general, potent narcotics such as hydromorphone should not be combined with alcohol.', 'Synergism', 'Patients taking sustained-release formulations of hydromorphone should not consume alcohol or use medications that contain alcohol on days of hydromorphone dosing.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336289, 'Hydroxyzine', 'Ethanol', 'Moderate', 'Alcohol may potentiate some of the pharmacologic effects of central nervous system (CNS)-active agents. Use in combination may result in additive CNS depression and/or impairment of judgment, thinking, and psychomotor skills.', 'DDInter', 1767317475, 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', 'Synergism', 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336290, 'Hyoscyamine', 'Ethanol', 'Moderate', 'Use of anticholinergic agents with alcohol may result in sufficient impairment of attention so as to render driving and operating machinery more hazardous. In addition, the potential for abuse may be increased with the combination. The mechanism of interaction is not established but may involve additive depressant effects on the central nervous system.', 'DDInter', 1767317475, 'Alcohol should generally be avoided during therapy with anticholinergic agents. Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Alcohol should generally be avoided during therapy with anticholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336291, 'Idelalisib', 'Ethanol', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal.', 'DDInter', 1767317475, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with idelalisib should have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.', 'Synergism', 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336292, 'Ifosfamide', 'Ethanol', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', 'DDInter', 1767317475, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
