INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336378, 'Ethanol', 'Milnacipran', 'Moderate', 'Use of milnacipran in conjunction with chronic alcohol consumption may potentiate the risk of liver injury. Milnacipran alone can increase serum transaminase levels. There have been reported cases of increased liver enzymes and severe liver injury, including fulminant hepatitis, from foreign postmarketing experience with milnacipran. Significant underlying clinical conditions and/or use of multiple concomitant medications were present in the cases of severe liver injury.', 'DDInter', 1767317475, 'Due to the risk of liver injury, milnacipran should generally not be prescribed to patients with substantial alcohol use.', 'Synergism', 'Due to the risk of liver injury, milnacipran should generally not be prescribed to patients with substantial alcohol use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
