INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1336420, 'Ethanol', 'Pentobarbital', 'Major', 'Concurrent acute use of barbiturates and ethanol may result in additive CNS effects, including impaired coordination, sedation, and death. Tolerance of these agents may occur with chronic use. The mechanism is related to inhibition of microsomal enzymes acutely and induction of hepatic microsomal enzymes chronically.', 'DDInter', 1767317475, 'The combination of ethanol and barbiturates should be avoided.', 'Metabolism', 'The combination of ethanol and barbiturates should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
